import React, { useState } from 'react';
import './App.css';

interface Clip {
  id: number;
  title: string;
  description: string;
  start_time: string;
  end_time: string;
  duration: number;
  timestamp: number;
  filename: string;
}

function App() {
  const [ytLink, setYtLink] = useState('');
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState('');
  const [clips, setClips] = useState<Clip[]>([]);
  const [numClips, setNumClips] = useState(3);
  const [clipDuration, setClipDuration] = useState(30);

  const handleAnalyze = async () => {
    if (!ytLink) {
      setStatus('Please enter a YouTube link');
      return;
    }

    setLoading(true);
    setStatus('Processing video...');
    setClips([]);

    try {
      const response = await fetch('http://localhost:5000/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          ytLink,
          numClips,
          clipDuration
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setStatus('Viral clip analysis completed! Use the timestamps to create your clips.');
        setClips(data.clips || []);
      } else {
        setStatus(`Error: ${data.error}`);
      }
    } catch (error) {
      setStatus('Error connecting to server');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App" style={{ 
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0f0f23 0%, #1a1a2e 50%, #16213e 100%)',
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
      padding: '40px 20px'
    }}>
      <div style={{
        maxWidth: 1200,
        margin: '0 auto',
        background: 'rgba(30, 30, 50, 0.95)',
        borderRadius: 20,
        padding: 40,
        boxShadow: '0 20px 40px rgba(0, 0, 0, 0.3)',
        backdropFilter: 'blur(10px)',
        border: '1px solid rgba(255, 255, 255, 0.1)'
      }}>
        <div style={{ textAlign: 'center', marginBottom: 40 }}>
          <h1 style={{
            fontSize: '2.5rem',
            fontWeight: 700,
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            margin: '0 0 10px 0',
            letterSpacing: '-0.02em'
          }}>
            üé¨ Viral Clip Generator
          </h1>
          <p style={{
            fontSize: '1.1rem',
            color: '#a0a0a0',
            margin: 0,
            fontWeight: 400
          }}>
            Transform YouTube videos into viral social media clips
          </p>
        </div>

        <div style={{ marginBottom: 32 }}>
          <div style={{
            position: 'relative',
            maxWidth: 600,
            margin: '0 auto'
          }}>
            <input
              type="text"
              placeholder="Paste YouTube video link here..."
              value={ytLink}
              onChange={e => setYtLink(e.target.value)}
              style={{
                width: '100%',
                padding: '16px 20px',
                fontSize: 16,
                border: '2px solid #2d2d44',
                borderRadius: 12,
                outline: 'none',
                transition: 'all 0.3s ease',
                fontFamily: 'inherit',
                backgroundColor: '#1a1a2e',
                color: '#ffffff',
                boxShadow: '0 2px 10px rgba(0, 0, 0, 0.2)'
              }}
              onFocus={(e) => {
                e.target.style.borderColor = '#667eea';
                e.target.style.boxShadow = '0 0 0 3px rgba(102, 126, 234, 0.2)';
              }}
              onBlur={(e) => {
                e.target.style.borderColor = '#2d2d44';
                e.target.style.boxShadow = '0 2px 10px rgba(0, 0, 0, 0.2)';
              }}
              disabled={loading}
            />
          </div>
        </div>

        <div style={{
          display: 'flex',
          gap: 24,
          alignItems: 'center',
          justifyContent: 'center',
          flexWrap: 'wrap',
          marginBottom: 32
        }}>
          <div style={{ textAlign: 'center' }}>
            <label style={{
              display: 'block',
              marginBottom: 8,
              fontSize: 14,
              fontWeight: 600,
              color: '#e0e0e0'
            }}>
              Number of Clips
            </label>
            <select 
              value={numClips} 
              onChange={e => setNumClips(Number(e.target.value))}
              disabled={loading}
              style={{
                padding: '12px 16px',
                fontSize: 16,
                border: '2px solid #2d2d44',
                borderRadius: 8,
                outline: 'none',
                backgroundColor: '#1a1a2e',
                color: '#ffffff',
                cursor: 'pointer',
                fontFamily: 'inherit',
                minWidth: 120
              }}
            >
              <option value={1}>1 Clip</option>
              <option value={2}>2 Clips</option>
              <option value={3}>3 Clips</option>
              <option value={4}>4 Clips</option>
              <option value={5}>5 Clips</option>
            </select>
          </div>

          <div style={{ textAlign: 'center' }}>
            <label style={{
              display: 'block',
              marginBottom: 8,
              fontSize: 14,
              fontWeight: 600,
              color: '#e0e0e0'
            }}>
              Clip Duration
            </label>
            <select 
              value={clipDuration} 
              onChange={e => setClipDuration(Number(e.target.value))}
              disabled={loading}
              style={{
                padding: '12px 16px',
                fontSize: 16,
                border: '2px solid #2d2d44',
                borderRadius: 8,
                outline: 'none',
                backgroundColor: '#1a1a2e',
                color: '#ffffff',
                cursor: 'pointer',
                fontFamily: 'inherit',
                minWidth: 140
              }}
            >
              <option value={15}>15 seconds</option>
              <option value={30}>30 seconds</option>
              <option value={45}>45 seconds</option>
              <option value={60}>60 seconds</option>
            </select>
          </div>

          <button 
            onClick={handleAnalyze} 
            style={{ 
              padding: '16px 32px', 
              fontSize: 16,
              fontWeight: 600,
              background: loading 
                ? '#4a4a6a' 
                : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
              color: 'white',
              border: 'none',
              borderRadius: 12,
              cursor: loading ? 'not-allowed' : 'pointer',
              transition: 'all 0.3s ease',
              boxShadow: loading 
                ? 'none' 
                : '0 4px 15px rgba(102, 126, 234, 0.3)',
              transform: loading ? 'none' : 'translateY(0)',
              minWidth: 180
            }}
            onMouseEnter={(e) => {
              if (!loading) {
                e.currentTarget.style.transform = 'translateY(-2px)';
                e.currentTarget.style.boxShadow = '0 6px 20px rgba(102, 126, 234, 0.4)';
              }
            }}
            onMouseLeave={(e) => {
              if (!loading) {
                e.currentTarget.style.transform = 'translateY(0)';
                e.currentTarget.style.boxShadow = '0 4px 15px rgba(102, 126, 234, 0.3)';
              }
            }}
            disabled={loading}
          >
            {loading ? '‚è≥ Processing...' : 'üöÄ Generate Viral Clips'}
          </button>
        </div>

        <div style={{ marginTop: 24, minHeight: 40, textAlign: 'center' }}>
          {status && (
            <div style={{
              padding: '12px 20px',
              borderRadius: 8,
              fontSize: 14,
              fontWeight: 500,
              backgroundColor: status.includes('Error') ? 'rgba(220, 38, 38, 0.1)' : 'rgba(22, 163, 74, 0.1)',
              color: status.includes('Error') ? '#fca5a5' : '#86efac',
              border: `1px solid ${status.includes('Error') ? 'rgba(220, 38, 38, 0.3)' : 'rgba(22, 163, 74, 0.3)'}`
            }}>
              {status}
            </div>
          )}
        </div>

        {clips.length > 0 && (
          <div style={{ marginTop: 48 }}>
            <h2 style={{
              fontSize: '1.8rem',
              fontWeight: 700,
              color: '#ffffff',
              margin: '0 0 32px 0',
              textAlign: 'center'
            }}>
              üéØ Your Viral Clips ({clips.length})
            </h2>
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
              gap: 24
            }}>
              {clips.map((clip, index) => (
                <div key={index} style={{ 
                  background: '#1a1a2e',
                  border: '1px solid #2d2d44',
                  borderRadius: 16,
                  padding: 24,
                  boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.3), 0 2px 4px -1px rgba(0, 0, 0, 0.2)',
                  transition: 'all 0.3s ease',
                  position: 'relative',
                  overflow: 'hidden'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = 'translateY(-4px)';
                  e.currentTarget.style.boxShadow = '0 10px 25px -3px rgba(0, 0, 0, 0.3), 0 4px 6px -2px rgba(0, 0, 0, 0.2)';
                  e.currentTarget.style.borderColor = '#3d3d54';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'translateY(0)';
                  e.currentTarget.style.boxShadow = '0 4px 6px -1px rgba(0, 0, 0, 0.3), 0 2px 4px -1px rgba(0, 0, 0, 0.2)';
                  e.currentTarget.style.borderColor = '#2d2d44';
                }}>
                  <div style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    height: 4,
                    background: 'linear-gradient(90deg, #667eea 0%, #764ba2 100%)'
                  }} />

                  <h3 style={{
                    margin: '0 0 12px 0',
                    fontSize: '1.1rem',
                    fontWeight: 600,
                    color: '#ffffff',
                    lineHeight: 1.4
                  }}>
                    {clip.title}
                  </h3>

                  <p style={{
                    margin: '0 0 16px 0',
                    fontSize: 14,
                    color: '#a0a0a0',
                    fontWeight: 500
                  }}>
                    ‚è±Ô∏è Duration: {clip.start_time} - {clip.end_time}
                  </p>

                  <p style={{
                    margin: '0 0 20px 0',
                    fontSize: 14,
                    color: '#c0c0c0',
                    lineHeight: 1.6
                  }}>
                    {clip.description}
                  </p>

                  <video 
                    controls 
                    style={{
                      width: '100%',
                      maxHeight: 200,
                      borderRadius: 8,
                      marginBottom: 16,
                      boxShadow: '0 2px 8px rgba(0, 0, 0, 0.3)',
                      backgroundColor: '#000000'
                    }}
                    src={`http://localhost:5000/clips/${clip.filename}`}
                  />

                  <a
                    href={`http://localhost:5000/download/${clip.filename}`}
                    download
                    style={{
                      display: 'inline-flex',
                      alignItems: 'center',
                      gap: 8,
                      padding: '12px 20px',
                      backgroundColor: '#10b981',
                      color: 'white',
                      border: 'none',
                      borderRadius: 8,
                      cursor: 'pointer',
                      fontSize: 14,
                      fontWeight: 600,
                      textDecoration: 'none',
                      transition: 'all 0.3s ease',
                      boxShadow: '0 2px 4px rgba(16, 185, 129, 0.3)'
                    }}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.backgroundColor = '#059669';
                      e.currentTarget.style.transform = 'translateY(-1px)';
                      e.currentTarget.style.boxShadow = '0 4px 8px rgba(16, 185, 129, 0.4)';
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.backgroundColor = '#10b981';
                      e.currentTarget.style.transform = 'translateY(0)';
                      e.currentTarget.style.boxShadow = '0 2px 4px rgba(16, 185, 129, 0.3)';
                    }}
                  >
                    üì• Download Clip
                  </a>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
