import React, { useState } from 'react';
import './App.css';

function App() {
  const [ytLink, setYtLink] = useState('');
  const [status, setStatus] = useState('');

  const handleAnalyze = async () => {
    if (!ytLink) {
      setStatus('Please enter a YouTube link.');
      return;
    }
    setStatus('Analyzing video...');
    try {
      const res = await fetch('http://localhost:5000/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ytLink }),
      });
      const data = await res.json();
      setStatus(data.message || JSON.stringify(data));
    } catch (err) {
      setStatus('Error contacting backend.');
    }
  };

  return (
    <div className="App" style={{ padding: 32 }}>
      <h2>YouTube Video Analyzer & Clipper</h2>
      <input
        type="text"
        placeholder="Paste YouTube video link here"
        value={ytLink}
        onChange={e => setYtLink(e.target.value)}
        style={{ width: 400, padding: 8, fontSize: 16 }}
      />
      <button onClick={handleAnalyze} style={{ marginLeft: 12, padding: '8px 16px', fontSize: 16 }}>
        Analyze
      </button>
      <div style={{ marginTop: 24, minHeight: 40 }}>{status}</div>
    </div>
  );
}

export default App;
