import React, { useState, useRef } from 'react';
import './App.css';

interface Clip {
  id: number;
  title: string;
  description: string;
  start_time: string;
  end_time: string;
  duration: number;
  timestamp: number;
  filename: string;
  captionStyle?: string;
  fontStyle?: string;
  isViral?: boolean;
  fileSize?: number;
  failed?: boolean;
  error?: string;
  hook_type?: string;
  viral_score?: number;
  natural_flow_score?: number;
  quality_rating?: string;
  analysis_method?: string;
  post_analysis?: {
    quality_check: string;
    actual_content: string;
    language_confirmed: string;
  };
}

// SVG Icons as components
const SparkleIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M12 0l3.09 6.26L22 9.27l-5.45 5.32L18.18 24 12 20.27 5.82 24l1.63-9.41L2 9.27l6.91-3.01L12 0z"/>
  </svg>
);

const RocketIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M12 2L13.09 8.26L22 12L13.09 15.74L12 22L10.91 15.74L2 12L10.91 8.26L12 2Z"/>
  </svg>
);

const VideoIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M8 5v14l11-7z"/>
  </svg>
);

const ClockIcon = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
    <circle cx="12" cy="12" r="10"/>
    <polyline points="12,6 12,12 16,14"/>
  </svg>
);

const DownloadIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"/>
    <polyline points="7,10 12,15 17,10"/>
    <line x1="12" y1="15" x2="12" y2="3"/>
  </svg>
);

const LoadingIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor" className="animate-spin">
    <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" strokeDasharray="32" strokeDashoffset="32">
      <animate attributeName="stroke-dashoffset" dur="1s" values="32;0" repeatCount="indefinite"/>
    </circle>
  </svg>
);

const CheckIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <polyline points="20,6 9,17 4,12"/>
  </svg>
);

function App() {
  const [url, setUrl] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [clips, setClips] = useState<Clip[]>([]);
  const [numClips, setNumClips] = useState(3);
  const [clipDuration, setClipDuration] = useState(30);
  const [captionsEnabled, setCaptionsEnabled] = useState(true);
  const [captionStyle, setCaptionStyle] = useState('single-word');
  const [playingIndex, setPlayingIndex] = useState<number | null>(null);
  const [mutedIndex, setMutedIndex] = useState<number | null>(null);
  const [loadingVideos, setLoadingVideos] = useState<Set<number>>(new Set());
  const videoRefs = useRef<(HTMLVideoElement | null)[]>([]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      console.log('Sending request to server...');
      const response = await fetch('http://localhost:5000/analyze-viral', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ytLink: url,
          numClips: numClips,
          clipDuration: clipDuration,
          captionStyle: captionsEnabled ? captionStyle : 'none',
          fontStyle: 'impact'
        }),
      });

      console.log('Response status:', response.status);
      const data = await response.json();
      console.log('Response data:', data);

      if (data.clips) {
        console.log('Setting clips:', data.clips);
        setClips(data.clips);
      } else {
        console.warn('No clips returned from server');
      }
    } catch (error) {
      console.error('Error during fetch:', error);
      alert('Failed to generate clips. Please check if the server is running on localhost:5000');
    } finally {
      setIsLoading(false);
    }
  };

  // Add a function to test server connectivity
  const testServer = async () => {
    try {
      const response = await fetch('http://localhost:5000/');
      const data = await response.json();
      console.log('Server test successful:', data);

      // Also test clips endpoint
      const clipsResponse = await fetch('http://localhost:5000/clips-list');
      const clipsData = await clipsResponse.json();
      console.log('Available clips:', clipsData);
    } catch (error) {
      console.error('Server test failed:', error);
    }
  };

  // Test server connectivity on component mount
  React.useEffect(() => {
    testServer();
  }, []);

  // Initialize loading state when clips change
  React.useEffect(() => {
    if (clips.length > 0) {
      setLoadingVideos(new Set(clips.map((_, index) => index)));
    }
  }, [clips]);

  // Example clips from actual generated content
  const exampleClips = [
    {
      title: "ðŸ”¥ Viral Business Insights",
      description: "AI-powered content that drives engagement",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751719352347_1.mp4",
      category: "Business"
    },
    {
      title: "ðŸ’¡ Game-Changing Strategies", 
      description: "Content optimized for maximum viral potential",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751719352347_2.mp4",
      category: "Strategy"
    },
    {
      title: "ðŸš€ Success Mindset Clips",
      description: "Engaging moments that capture attention",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751719352347_3.mp4",
      category: "Mindset"
    },
    {
      title: "âš¡ High-Impact Content",
      description: "Perfectly timed viral content creation",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751660345508_1.mp4",
      category: "Impact"
    },
    {
      title: "ðŸŽ¯ Targeted Messaging",
      description: "AI-selected peak engagement moments",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751660345508_2.mp4",
      category: "Marketing"
    },
    {
      title: "âœ¨ Premium Quality Clips",
      description: "Professional-grade viral content",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751660345508_3.mp4",
      category: "Quality"
    }
  ];

  return (
    <div className="app-container">
      {/* Header */}
      <header className="header">
        <div className="header-content">
          <div className="logo-container">
            <img src="/logo-removebg-preview.png" alt="Viral Clip Generator" className="logo-image" />
            <span className="brand-text">Trilion</span>
          </div>
          <nav className="nav-menu">
            <a href="#features" className="nav-link">Features</a>
            <a href="#examples" className="nav-link">Examples</a>
            <a href="#generate" className="nav-link cta-link">Generate Clips</a>
          </nav>
        </div>
      </header>

      {/* Hero Section */}
      <section className="hero">
        <div className="hero-background"></div>
        <div className="hero-content">
          <div className="hero-badge">
            <SparkleIcon />
            <span>AI-Powered Viral Content Creation</span>
          </div>
          <h1 className="hero-title">
            Transform Your Videos into
            <span className="gradient-text"> Viral Clips</span>
          </h1>
          <p className="hero-description">
            Our advanced AI analyzes your content to identify and extract the most engaging moments, 
            creating perfectly timed clips optimized for maximum viral potential on social media.
          </p>
          <div className="hero-stats">
            <div className="stat-item">
              <div className="stat-number">10M+</div>
              <div className="stat-label">Clips Generated</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">98%</div>
              <div className="stat-label">Accuracy Rate</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">50x</div>
              <div className="stat-label">Faster Creation</div>
            </div>
          </div>
        </div>
        <div className="hero-visual">
          <div className="floating-elements">
            <div className="floating-icon" style={{animationDelay: '0s'}}><VideoIcon /></div>
            <div className="floating-icon" style={{animationDelay: '2s'}}><RocketIcon /></div>
            <div className="floating-icon" style={{animationDelay: '4s'}}><SparkleIcon /></div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section id="features" className="features-section">
        <div className="features-container">
          <h2 className="section-title">Why Choose Our AI Clip Generator?</h2>
          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-icon gradient-bg-1">
                <SparkleIcon />
              </div>
              <h3>AI-Powered Analysis</h3>
              <p>Advanced machine learning identifies viral moments with 98% accuracy</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon gradient-bg-2">
                <RocketIcon />
              </div>
              <h3>Instant Generation</h3>
              <p>Create professional viral clips in seconds, not hours</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon gradient-bg-3">
                <VideoIcon />
              </div>
              <h3>Platform Optimized</h3>
              <p>Perfect formatting for TikTok, Instagram Reels, and YouTube Shorts</p>
            </div>
          </div>
        </div>
      </section>

      {/* Main Content */}
      <main className="main-content">
        {/* Input Section */}
        <section id="generate" className="generator-section">
          <div className="generator-container">
            <div className="generator-header">
              <h2 className="section-title">Create Your Viral Clips</h2>
              <p className="section-subtitle">
                Paste your YouTube URL and let our AI create engaging clips in seconds
              </p>
            </div>

            <form onSubmit={handleSubmit} className="generator-form">
              <div className="input-container">
                <div className="input-wrapper">
                  <input
                    type="text"
                    className="url-input"
                    placeholder="Paste your YouTube URL here..."
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    required
                  />
                  <div className="input-icon">
                    <VideoIcon />
                  </div>
                </div>
                <button type="submit" className="submit-button" disabled={isLoading || !url}>
                  {isLoading ? (
                    <>
                      <LoadingIcon />
                      <span>Processing...</span>
                    </>
                  ) : (
                    <>
                      <RocketIcon />
                      <span>Create Viral Clips</span>
                    </>
                  )}
                </button>
              </div>

              {/* Options Section */}
              <div className="options-container">
                <div className="option-group">
                  <label className="option-label">
                    <ClockIcon />
                    Number of Clips
                  </label>
                  <select 
                    className="option-select"
                    value={numClips}
                    onChange={(e) => setNumClips(parseInt(e.target.value))}
                  >
                    <option value={1}>1 clip</option>
                    <option value={2}>2 clips</option>
                    <option value={3}>3 clips</option>
                    <option value={4}>4 clips</option>
                    <option value={5}>5 clips</option>
                  </select>
                </div>

                <div className="option-group">
                  <label className="option-label">
                    <ClockIcon />
                    Clip Duration
                  </label>
                  <select 
                    className="option-select"
                    value={clipDuration}
                    onChange={(e) => setClipDuration(parseInt(e.target.value))}
                  >
                    <option value={15}>15 seconds</option>
                    <option value={30}>30 seconds</option>
                    <option value={60}>60 seconds</option>
                  </select>
                </div>

                <div className="option-group">
                  <label className="option-label">
                    <SparkleIcon />
                    Captions
                  </label>
                  <div className="toggle-container">
                    <label className="toggle-switch">
                      <input
                        type="checkbox"
                        checked={captionsEnabled}
                        onChange={(e) => setCaptionsEnabled(e.target.checked)}
                      />
                      <span className="toggle-slider"></span>
                    </label>
                    <span className="toggle-text">{captionsEnabled ? 'On' : 'Off'}</span>
                  </div>
                </div>

                {captionsEnabled && (
                  <div className="option-group">
                    <label className="option-label">
                      <SparkleIcon />
                      Caption Style
                    </label>
                    <select 
                      className="option-select"
                      value={captionStyle}
                      onChange={(e) => setCaptionStyle(e.target.value)}
                    >
                      <option value="single-word">Single Word</option>
                      <option value="engaging">Engaging Phrases</option>
                    </select>
                  </div>
                )}
              </div>
            </form>
          </div>
        </section>

        {/* Loading State */}
        {isLoading && (
          <section className="loading-section">
            <div className="loading-container">
              <div className="loading-animation">
                <div className="loading-spinner"></div>
                <div className="loading-dots">
                  <div className="dot"></div>
                  <div className="dot"></div>
                  <div className="dot"></div>
                </div>
              </div>
              <h3>Creating Your Viral Clips</h3>
              <p>Our AI is analyzing your video and extracting the most engaging moments...</p>
              <div className="progress-steps">
                <div className="step active">
                  <CheckIcon />
                  <span>Video Downloaded</span>
                </div>
                <div className="step active">
                  <LoadingIcon />
                  <span>AI Analysis</span>
                </div>
                <div className="step">
                  <span>Clip Generation</span>
                </div>
              </div>
            </div>
          </section>
        )}

        {/* Example Clips Carousel */}
        <section id="examples" className="carousel-section">
          <div className="carousel-container">
            <div className="carousel-header">
              <h2 className="section-title">See Our AI in Action</h2>
              <p className="section-subtitle">
                Real clips generated by our AI from actual YouTube videos
              </p>
            </div>

            <div className="carousel-wrapper">
              <div className="carousel-track">
                {[...exampleClips, ...exampleClips].map((clip, index) => (
                  <div key={index} className="carousel-card">
                    <div className="carousel-video-container">
                      <div style={{position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', background: '#000', zIndex: 0}}></div>
                      <video
                        className="carousel-video"
                        ref={el => { videoRefs.current[index] = el; }}
                        muted={mutedIndex !== index}
                        controls={false}
                        playsInline
                        style={{position: 'relative', zIndex: 1}}
                        onClick={() => {
                          const video = videoRefs.current[index];
                          if (!video) return;
                          if (playingIndex === index) {
                            video.pause();
                            setPlayingIndex(null);
                          } else {
                            videoRefs.current.forEach((v, i) => {
                              if (v && i !== index) v.pause();
                            });
                            video.play();
                            setPlayingIndex(index);
                            setMutedIndex(index);
                            video.muted = false;
                          }
                        }}
                        onPlay={() => {
                          setPlayingIndex(index);
                          setMutedIndex(index);
                        }}
                        onPause={() => {
                          setPlayingIndex(null);
                        }}
                      >
                        <source src={clip.videoUrl} type="video/mp4" />
                      </video>
                    </div>
                    <div className="carousel-info">
                      <h4>{clip.title}</h4>
                      <p>{clip.description}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </section>

        {/* Test Section - Add this temporarily for debugging */}
        <section className="test-section" style={{ padding: '2rem', background: '#1a1a1a', margin: '2rem 0' }}>
          <h2>ðŸ”§ Debug Test Section</h2>
          <p>Testing video loading with an existing clip:</p>
          <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
            <button 
              onClick={() => {
                fetch('http://localhost:5000/clips-list')
                  .then(r => r.json())
                  .then(data => {
                    console.log('Available clips:', data);
                    alert(`Found ${data.clips.length} clips in media folder`);
                  })
                  .catch(e => {
                    console.error('Failed to fetch clips:', e);
                    alert('Failed to connect to server');
                  });
              }}
              style={{ padding: '0.5rem 1rem', background: '#333', color: 'white', border: 'none', borderRadius: '4px' }}
            >
              Test Server Connection
            </button>

            <button 
              onClick={() => {
                const testUrl = 'http://localhost:5000/clips/VIRAL_single-word_1751776101577_1.mp4';
                console.log('Testing direct video access:', testUrl);
                fetch(testUrl, { method: 'HEAD' })
                  .then(response => {
                    console.log('Video HEAD response:', response.status, response.headers);
                    alert(`Video accessible: ${response.ok ? 'Yes' : 'No'} (${response.status})`);
                  })
                  .catch(e => {
                    console.error('Video access test failed:', e);
                    alert('Video access failed');
                  });
              }}
              style={{ padding: '0.5rem 1rem', background: '#333', color: 'white', border: 'none', borderRadius: '4px' }}
            >
              Test Video Access
            </button>
          </div>

          <div style={{ marginTop: '1rem' }}>
            <h3>Direct Video Test:</h3>
            <video 
              controls 
              style={{ width: '300px', height: '200px', background: '#000' }}
              onLoadStart={() => console.log('Test video loading started')}
              onLoadedData={() => console.log('Test video loaded successfully')}
              onError={(e) => console.error('Test video error:', e)}
            >
              <source src="http://localhost:5000/clips/VIRAL_single-word_1751776101577_1.mp4" type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
        </section>

        {/* Generated Clips Results */}
        {clips.length > 0 && (
          <section className="results-section">
            <div className="results-container">
              {/* Success Message */}
              <div className="results-success">
                <h3>ðŸŽ‰ Success! Your viral clips are ready</h3>
                <p>Our AI has analyzed your video and created {clips.length} optimized clips for maximum engagement</p>
              </div>

              <div className="results-header">
                <h2 className="section-title">Your Viral Clips</h2>
                <p className="section-subtitle">
                  AI-powered clips optimized for maximum engagement and viral potential
                </p>
              </div>

              <div className="results-grid">
                {clips.map((clip, index) => (
                  <div key={index} className="result-card">
                    <div className="result-video-container">
                      <div style={{position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', background: '#000', zIndex: 0}}></div>
                      <video 
                        className="result-video vertical-video" 
                        controls 
                        preload="metadata"
                        style={{position: 'relative', zIndex: 1}}
                        onLoadStart={() => {
                          console.log(`Video ${index + 1} loading started`);
                          setLoadingVideos(prev => new Set(prev).add(index));
                        }}
                        onLoadedData={() => {
                          console.log(`Video ${index + 1} loaded successfully`);
                          setLoadingVideos(prev => {
                            const newSet = new Set(prev);
                            newSet.delete(index);
                            return newSet;
                          });
                        }}
                        onError={(e) => {
                          console.error(`Video ${index + 1} error:`, e);
                          const videoElement = e.target as HTMLVideoElement;
                          if (videoElement && videoElement.error) {
                            console.error(`Video ${index + 1} error details:`, {
                              code: videoElement.error.code,
                              message: videoElement.error.message
                            });
                          }
                          setLoadingVideos(prev => {
                            const newSet = new Set(prev);
                            newSet.delete(index);
                            return newSet;
                          });
                        }}
                        onCanPlay={() => {
                          console.log(`Video ${index + 1} can play`);
                          setLoadingVideos(prev => {
                            const newSet = new Set(prev);
                            newSet.delete(index);
                            return newSet;
                          });
                        }}
                      >
                        <source 
                          src={`http://localhost:5000/clips/${clip.filename}`} 
                          type="video/mp4"
                          onError={(e) => console.error(`Source error for ${clip.filename}:`, e)}
                        />
                        Your browser does not support the video tag.
                      </video>
                      {/* Show loading indicator only when video is loading */}
                      {loadingVideos.has(index) && (
                        <div style={{
                          position: 'absolute',
                          top: '50%',
                          left: '50%',
                          transform: 'translate(-50%, -50%)',
                          color: 'white',
                          textAlign: 'center',
                          zIndex: 2,
                          background: 'rgba(0, 0, 0, 0.7)',
                          padding: '1rem',
                          borderRadius: '8px'
                        }}>
                          <div style={{ marginBottom: '0.5rem' }}>
                            <div className="loading-spinner" style={{ margin: '0 auto', width: '20px', height: '20px' }}></div>
                          </div>
                          <p style={{ margin: 0 }}>Loading clip...</p>
                          <small style={{ opacity: 0.7 }}>{clip.filename}</small>
                        </div>
                      )}
                    </div>
                    <div className="result-info">
                      <h3>ðŸŽ¬ Viral Clip #{index + 1}</h3>
                      <p>
                        {clip.description || `Engaging ${clip.duration || clipDuration}s clip optimized for viral growth and maximum audience engagement.`}
                      </p>
                      <div className="result-actions">
                        <a 
                          href={`http://localhost:5000/clips/${clip.filename}`}
                          className="action-button download"
                          download={`viral_clip_${index + 1}.mp4`}
                          onClick={(e) => {
                            console.log(`Downloading clip: ${clip.filename}`);
                            // Test if file exists first
                            fetch(`http://localhost:5000/clips/${clip.filename}`, {method: 'HEAD'})
                              .then(response => {
                                if (!response.ok) {
                                  e.preventDefault();
                                  alert('File not found on server');
                                }
                              })
                              .catch(error => {
                                console.error('Download test failed:', error);
                                e.preventDefault();
                                alert('Cannot access file');
                              });
                          }}
                        >
                          <DownloadIcon />
                          Download HD
                        </a>
                        <button 
                          className="action-button share"
                          onClick={() => {
                            const clipUrl = `http://localhost:5000/clips/${clip.filename}`;
                            console.log('Sharing clip:', clipUrl);
                            if (navigator.share) {
                              navigator.share({
                                title: `Viral Clip #${index + 1}`,
                                text: 'Check out this AI-generated viral clip!',
                                url: clipUrl
                              });
                            } else {
                              navigator.clipboard.writeText(clipUrl);
                              alert('Link copied to clipboard!');
                            }
                          }}
                        >
                          <SparkleIcon />
                          Share
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </section>
        )}
      </main>
    </div>
  );
}

export default App;
