import React, { useState } from 'react';
import './App.css';

interface AnalysisData {
  viral_hooks: string[];
  important_topics: string[];
  summary: string;
  clip_suggestions: Array<{
    start_time: string;
    end_time: string;
    title: string;
    description: string;
  }>;
}

interface Clip {
  filename: string;
  title: string;
  description: string;
  start_time: string;
  end_time: string;
}

function App() {
  const [ytLink, setYtLink] = useState('');
  const [status, setStatus] = useState('');
  const [analysis, setAnalysis] = useState<AnalysisData | null>(null);
  const [clips, setClips] = useState<Clip[]>([]);
  const [loading, setLoading] = useState(false);

  const handleAnalyze = async () => {
    if (!ytLink) {
      setStatus('Please enter a YouTube link.');
      return;
    }
    setLoading(true);
    setStatus('Analyzing video and creating viral clips...');
    setAnalysis(null);
    setClips([]);

    try {
      const res = await fetch('http://localhost:5000/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ytLink }),
      });
      const data = await res.json();

      if (data.error) {
        setStatus(`Error: ${data.error}`);
      } else {
        setStatus(data.message);
        // Try to parse the analysis JSON from Claude's response
        try {
          const analysisData = JSON.parse(data.analysis);
          setAnalysis(analysisData);
          setClips(data.clips || []);
        } catch (parseError) {
          setStatus('Analysis completed but could not parse results.');
        }
      }
    } catch (err) {
      setStatus('Error contacting backend.');
    } finally {
      setLoading(false);
    }
  };

  const downloadClip = (filename: string) => {
    const link = document.createElement('a');
    link.href = `http://localhost:5000/clip/${filename}`;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="App" style={{ padding: 32, maxWidth: 1200, margin: '0 auto' }}>
      <h2>YouTube Video Analyzer & Viral Clip Creator</h2>
      <div style={{ marginBottom: 24 }}>
        <input
          type="text"
          placeholder="Paste YouTube video link here"
          value={ytLink}
          onChange={e => setYtLink(e.target.value)}
          style={{ width: 400, padding: 8, fontSize: 16 }}
          disabled={loading}
        />
        <button 
          onClick={handleAnalyze} 
          style={{ 
            marginLeft: 12, 
            padding: '8px 16px', 
            fontSize: 16,
            backgroundColor: loading ? '#ccc' : '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: 4,
            cursor: loading ? 'not-allowed' : 'pointer'
          }}
          disabled={loading}
        >
          {loading ? 'Processing...' : 'Analyze & Create Clips'}
        </button>
      </div>

      <div style={{ marginTop: 24, minHeight: 40 }}>
        {status && <p style={{ color: status.includes('Error') ? 'red' : 'green' }}>{status}</p>}
      </div>

      {clips.length > 0 && (
        <div style={{ marginTop: 32, textAlign: 'left' }}>
          <h3>🎬 Created Viral Clips</h3>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))', gap: 24 }}>
            {clips.map((clip, index) => (
              <div key={index} style={{ 
                border: '2px solid #007bff', 
                padding: 20, 
                borderRadius: 12,
                backgroundColor: '#f8f9fa'
              }}>
                <h4 style={{ color: '#007bff', marginTop: 0 }}>{clip.title}</h4>
                <p><strong>Duration:</strong> {clip.start_time} - {clip.end_time}</p>

                {/* Video Preview */}
                <div style={{ marginBottom: 16 }}>
                  <video 
                    controls 
                    style={{ 
                      width: '100%', 
                      maxWidth: '300px', 
                      height: 'auto',
                      borderRadius: 8,
                      border: '1px solid #ddd'
                    }}
                    src={`http://localhost:5000/clip/${clip.filename}`}
                  >
                    Your browser does not support the video tag.
                  </video>
                </div>

                <p style={{ fontSize: 14, color: '#666', marginBottom: 16 }}>{clip.description}</p>

                <button 
                  onClick={() => downloadClip(clip.filename)}
                  style={{
                    backgroundColor: '#28a745',
                    color: 'white',
                    border: 'none',
                    padding: '8px 16px',
                    borderRadius: 4,
                    cursor: 'pointer',
                    fontSize: 14
                  }}
                >
                  📥 Download Clip
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {analysis && (
        <div style={{ marginTop: 32, textAlign: 'left' }}>
          <h3>📊 Analysis Results</h3>

          <div style={{ marginBottom: 24 }}>
            <h4>Summary</h4>
            <p>{analysis.summary}</p>
          </div>

          <div style={{ marginBottom: 24 }}>
            <h4>🎯 Viral Hooks</h4>
            <ul>
              {analysis.viral_hooks.map((hook, index) => (
                <li key={index}>{hook}</li>
              ))}
            </ul>
          </div>

          <div style={{ marginBottom: 24 }}>
            <h4>📈 Important Topics</h4>
            <ul>
              {analysis.important_topics.map((topic, index) => (
                <li key={index}>{topic}</li>
              ))}
            </ul>
          </div>

          <div>
            <h4>🎬 Clip Suggestions</h4>
            {analysis.clip_suggestions.map((clip, index) => (
              <div key={index} style={{ 
                border: '1px solid #ddd', 
                padding: 16, 
                marginBottom: 12, 
                borderRadius: 8 
              }}>
                <h5>{clip.title}</h5>
                <p><strong>Time:</strong> {clip.start_time} - {clip.end_time}</p>
                <p>{clip.description}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
