      category: "Marketing"
    },
    {
      title: "âœ¨ Premium Quality Clips",
      description: "Professional-grade viral content",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751660345508_3.mp4",
      category: "Quality"
    }
  ];

  return (
    <div className="app-container">
      <header className="header">Viral Clip Generator</header>
      <main>
        <section className="generator-section">
          <form onSubmit={handleSubmit} className="generator-form">
            <div className="input-container">
              <input
