import React, { useState } from 'react';
import './App.css';

interface Clip {
  id: number;
  title: string;
  description: string;
  start_time: string;
  end_time: string;
  duration: number;
  timestamp: number;
  filename: string;
  captionStyle?: string;
  fontStyle?: string;
  isViral?: boolean;
  fileSize?: number;
  failed?: boolean;
  error?: string;
  hook_type?: string;
  viral_score?: number;
  natural_flow_score?: number;
  quality_rating?: string;
  analysis_method?: string;
  post_analysis?: {
    quality_check: string;
    actual_content: string;
    language_confirmed: string;
  };
}

// SVG Icons as components
const SparkleIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M12 0l3.09 6.26L22 9.27l-5.45 5.32L18.18 24 12 20.27 5.82 24l1.63-9.41L2 9.27l6.91-3.01L12 0z"/>
  </svg>
);

const RocketIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M12 2L13.09 8.26L22 12L13.09 15.74L12 22L10.91 15.74L2 12L10.91 8.26L12 2Z"/>
  </svg>
);

const VideoIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M8 5v14l11-7z"/>
  </svg>
);

const ClockIcon = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
    <circle cx="12" cy="12" r="10"/>
    <polyline points="12,6 12,12 16,14"/>
  </svg>
);

const DownloadIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"/>
    <polyline points="7,10 12,15 17,10"/>
    <line x1="12" y1="15" x2="12" y2="3"/>
  </svg>
);

const FolderIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <path d="M22 19a2 2 0 0 1-2 2H4a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h5l2 3h9a2 2 0 0 1 2 2z"/>
  </svg>
);

const LoadingIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor" className="animate-spin">
    <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" strokeDasharray="32" strokeDashoffset="32">
      <animate attributeName="stroke-dashoffset" dur="1s" values="32;0" repeatCount="indefinite"/>
    </circle>
  </svg>
);

const CheckIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <polyline points="20,6 9,17 4,12"/>
  </svg>
);

const CloseIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <line x1="18" y1="6" x2="6" y2="18"/>
    <line x1="6" y1="6" x2="18" y2="18"/>
  </svg>
);

function App() {
  const [url, setUrl] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [clips, setClips] = useState<Clip[]>([]);
  const [numClips, setNumClips] = useState(3);
  const [clipDuration, setClipDuration] = useState(30);
  const [captionsEnabled, setCaptionsEnabled] = useState(true);
  const [captionStyle, setCaptionStyle] = useState('single-word');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:5000/analyze-viral', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ytLink: url,
          numClips: 3,
          captionStyle: 'single-word',
          fontStyle: 'impact'
        }),
      });

      const data = await response.json();
      if (data.clips) {
        setClips(data.clips);
      }
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Example clips from the provided video
  const exampleClips = [
    {
      title: "The World's Changing",
      description: "AI's coming for jobs. Inflation is killing savings. Governments are collapsing...",
      videoUrl: "/clips/VIRAL_single-word_1751659195171_1.mp4"
    },
    {
      title: "Break Free from Conditioning",
      description: "You've been conditioned to stay in line. Stay quiet, stay average. But deep down that fire still burns...",
      videoUrl: "/clips/VIRAL_single-word_1751659195171_2.mp4"
    }
  ];

  return (
    <div className="app-container">
      {/* Header */}
      <header className="header">
        <div className="header-content">
          <img src="/trilion-logo.png" alt="Trilion" className="logo" />
        </div>
      </header>

      {/* Hero Section */}
      <section className="hero">
        <h1>Transform Your Videos into Viral Clips</h1>
        <p>Using advanced AI to identify and extract the most engaging moments from your videos</p>
      </section>

      {/* Main Content */}
      <main className="main-content">
        {/* Input Section */}
        <section className="input-section">
          <form onSubmit={handleSubmit}>
            <div className="input-container">
            <input
              type="text"
                className="url-input"
                placeholder="Paste your YouTube URL here..."
                value={url}
                onChange={(e) => setUrl(e.target.value)}
              />
              <button type="submit" className="submit-button" disabled={isLoading}>
                {isLoading ? 'Processing...' : 'Create Viral Clips'}
          </button>
            </div>
          </form>
        </section>

        {/* Generated Clips Section */}
        {isLoading && (
          <div className="loading">
            <div className="loading-spinner"></div>
          </div>
        )}

        {clips.length > 0 && (
          <section className="example-section">
            <div className="example-header">
              <h2>Your Generated Clips</h2>
            </div>
            <div className="example-grid">
              {clips.map((clip, index) => (
                <div key={index} className="example-card">
                  <video className="example-video" controls>
                    <source src={`http://localhost:5000/clips/${clip.filename}`} type="video/mp4" />
                    Your browser does not support the video tag.
                  </video>
                  <div className="example-info">
                    <h3>Clip {index + 1}</h3>
                    <p>{clip.description || 'AI-generated viral clip'}</p>
                  </div>
                </div>
              ))}
            </div>
          </section>
        )}

        {/* Example Section */}
        <section className="example-section">
          <div className="example-header">
            <h2>Example Clips</h2>
            <p>See how our AI transforms videos into engaging viral content</p>
          </div>
          <div className="example-grid">
            {exampleClips.map((clip, index) => (
              <div key={index} className="example-card">
                <video className="example-video" controls>
                  <source src={clip.videoUrl} type="video/mp4" />
                  Your browser does not support the video tag.
                </video>
                <div className="example-info">
                  <h3>{clip.title}</h3>
                  <p>{clip.description}</p>
                </div>
              </div>
            ))}
      </div>
        </section>
      </main>
    </div>
  );
}

export default App;
