import React, { useState } from 'react';
import './App.css';

interface Clip {
  id: number;
  title: string;
  description: string;
  start_time: string;
  end_time: string;
  duration: number;
  timestamp: number;
  filename: string;
  captionStyle?: string;
  fontStyle?: string;
  isViral?: boolean;
  fileSize?: number;
}

// SVG Icons as components
const SparkleIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M12 0l3.09 6.26L22 9.27l-5.45 5.32L18.18 24 12 20.27 5.82 24l1.63-9.41L2 9.27l6.91-3.01L12 0z"/>
  </svg>
);

const RocketIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M12 2L13.09 8.26L22 12L13.09 15.74L12 22L10.91 15.74L2 12L10.91 8.26L12 2Z"/>
  </svg>
);

const VideoIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M8 5v14l11-7z"/>
  </svg>
);

const ClockIcon = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
    <circle cx="12" cy="12" r="10"/>
    <polyline points="12,6 12,12 16,14"/>
  </svg>
);

const DownloadIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"/>
    <polyline points="7,10 12,15 17,10"/>
    <line x1="12" y1="15" x2="12" y2="3"/>
  </svg>
);

const FolderIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <path d="M22 19a2 2 0 0 1-2 2H4a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h5l2 3h9a2 2 0 0 1 2 2z"/>
  </svg>
);

const LoadingIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor" className="animate-spin">
    <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" strokeDasharray="32" strokeDashoffset="32">
      <animate attributeName="stroke-dashoffset" dur="1s" values="32;0" repeatCount="indefinite"/>
    </circle>
  </svg>
);

const CheckIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <polyline points="20,6 9,17 4,12"/>
  </svg>
);

const CloseIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <line x1="18" y1="6" x2="6" y2="18"/>
    <line x1="6" y1="6" x2="18" y2="18"/>
  </svg>
);

function App() {
  const [ytLink, setYtLink] = useState('');
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState('');
  const [clips, setClips] = useState<Clip[]>([]);
  const [numClips, setNumClips] = useState(3);
  const [clipDuration, setClipDuration] = useState(30);
  const [useViralCaptions, setUseViralCaptions] = useState(true);
  const [captionStyle, setCaptionStyle] = useState('single-word');
  const [fontStyle, setFontStyle] = useState('impact');


  const handleAnalyze = async () => {
    if (!ytLink) {
      setStatus('Please enter a YouTube link');
      return;
    }

    setLoading(true);
    setStatus('Processing video...');
    setClips([]);

    try {
      // Choose endpoint based on viral caption setting
      const endpoint = useViralCaptions ? 
        'http://localhost:5000/analyze-viral' : 
        'http://localhost:5000/analyze';

      const requestBody = useViralCaptions ? {
        ytLink,
        numClips,
        clipDuration,
        captionStyle,
        fontStyle
      } : {
        ytLink,
        numClips,
        clipDuration
      };

      const response = await fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });

      const data = await response.json();

      if (response.ok) {
        if (useViralCaptions) {
          setStatus(`${data.clips?.length || 0} viral clips created with ${captionStyle} captions!`);
        } else {
          setStatus('Viral clip analysis completed! Use the timestamps to create your clips.');
        }
        setClips(data.clips || []);
      } else {
        setStatus(`Error: ${data.error}`);
      }
    } catch (error) {
      setStatus('Error connecting to server');
    } finally {
      setLoading(false);
    }
  };



  const fetchClipsList = async () => {
    try {
      const response = await fetch('http://localhost:5000/clips-list');
      const data = await response.json();

      if (response.ok) {
        // Convert the clips format to match our interface
        const formattedClips = data.clips.map((clip: any, index: number) => ({
          id: index,
          filename: clip.filename,
          title: clip.filename.includes('VIRAL_') ? 
            `VIRAL ${clip.filename}` : 
            clip.filename,
          description: clip.isViral ? 
            `Viral ${clip.captionStyle} captions` : 
            'Standard clip',
          start_time: '0:00',
          end_time: '0:30',
          duration: 30,
          timestamp: new Date(clip.created).getTime(),
          captionStyle: clip.captionStyle,
          isViral: clip.isViral,
          fileSize: clip.size
        }));

        setClips(formattedClips);
      }
    } catch (error) {
      console.error('Error fetching clips:', error);
    }
  };

  return (
    <div className="App" style={{ 
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0f172a 0%, #1e293b 50%, #0f172a 100%)',
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
      padding: '48px 24px',
      color: '#f8fafc',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Animated background elements */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'radial-gradient(circle at 20% 80%, rgba(59, 130, 246, 0.05) 0%, transparent 50%), radial-gradient(circle at 80% 20%, rgba(147, 51, 234, 0.05) 0%, transparent 50%), radial-gradient(circle at 40% 40%, rgba(239, 68, 68, 0.03) 0%, transparent 50%)',
        pointerEvents: 'none'
      }} />

      <div style={{
        maxWidth: 1200,
        margin: '0 auto',
        background: 'rgba(30, 41, 59, 0.8)',
        borderRadius: 24,
        padding: 64,
        boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.8), inset 0 1px 0 rgba(255, 255, 255, 0.1)',
        border: '1px solid rgba(148, 163, 184, 0.1)',
        backdropFilter: 'blur(20px)',
        position: 'relative'
      }}>
        {/* Header with premium styling */}
        <div style={{ textAlign: 'center', marginBottom: 64 }}>
          <div style={{
            display: 'inline-flex',
            alignItems: 'center',
            gap: 12,
            marginBottom: 32,
            padding: '12px 24px',
            background: 'rgba(59, 130, 246, 0.1)',
            borderRadius: 50,
            border: '1px solid rgba(59, 130, 246, 0.2)',
            backdropFilter: 'blur(10px)'
          }}>
            <SparkleIcon />
            <span style={{ 
              fontSize: 14, 
              fontWeight: 600, 
              color: '#3b82f6',
              textTransform: 'uppercase',
              letterSpacing: '2px'
            }}>
              AI-Powered
            </span>
          </div>

          <h1 style={{
            fontSize: '4rem',
            fontWeight: 900,
            background: 'linear-gradient(135deg, #f8fafc 0%, #3b82f6 25%, #8b5cf6 50%, #ef4444 75%, #f8fafc 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            margin: '0 0 24px 0',
            letterSpacing: '-0.04em',
            lineHeight: 1.1
          }}>
            Viral Clip Generator
          </h1>
          <p style={{
            fontSize: '1.25rem',
            color: '#cbd5e1',
            margin: 0,
            fontWeight: 400,
            opacity: 0.9,
            maxWidth: 600,
            marginLeft: 'auto',
            marginRight: 'auto',
            lineHeight: 1.6
          }}>
            Transform any YouTube video into viral social media clips with AI-powered analysis
          </p>
        </div>

        {/* Enhanced input section */}
        <div style={{ marginBottom: 56 }}>
          <div style={{
            position: 'relative',
            maxWidth: 800,
            margin: '0 auto'
          }}>
            <div style={{
              position: 'absolute',
              top: -10,
              left: 28,
              background: '#1e293b',
              padding: '0 16px',
              fontSize: 14,
              fontWeight: 600,
              color: '#3b82f6',
              textTransform: 'uppercase',
              letterSpacing: '1px',
              zIndex: 1
            }}>
              YouTube URL
            </div>
            <input
              type="text"
              placeholder="https://www.youtube.com/watch?v=..."
              value={ytLink}
              onChange={e => setYtLink(e.target.value)}
              style={{
                width: '100%',
                padding: '28px 32px',
                fontSize: 18,
                border: '2px solid rgba(148, 163, 184, 0.2)',
                borderRadius: 16,
                outline: 'none',
                transition: 'all 0.3s ease',
                fontFamily: 'inherit',
                backgroundColor: 'rgba(15, 23, 42, 0.6)',
                color: '#f8fafc',
                boxShadow: '0 4px 16px rgba(0, 0, 0, 0.2), inset 0 1px 0 rgba(255, 255, 255, 0.05)',
                backdropFilter: 'blur(10px)'
              }}
              onFocus={(e) => {
                e.target.style.borderColor = '#3b82f6';
                e.target.style.boxShadow = '0 0 0 4px rgba(59, 130, 246, 0.1), 0 4px 16px rgba(0, 0, 0, 0.2), inset 0 1px 0 rgba(255, 255, 255, 0.05)';
                e.target.style.transform = 'translateY(-2px)';
              }}
              onBlur={(e) => {
                e.target.style.borderColor = 'rgba(148, 163, 184, 0.2)';
                e.target.style.boxShadow = '0 4px 16px rgba(0, 0, 0, 0.2), inset 0 1px 0 rgba(255, 255, 255, 0.05)';
                e.target.style.transform = 'translateY(0)';
              }}
              disabled={loading}
            />
          </div>
        </div>

        {/* Enhanced controls section */}
        <div style={{
          display: 'flex',
          gap: 48,
          alignItems: 'center',
          justifyContent: 'center',
          flexWrap: 'wrap',
          marginBottom: 56,
          padding: '40px',
          background: 'rgba(15, 23, 42, 0.4)',
          borderRadius: 20,
          border: '1px solid rgba(148, 163, 184, 0.1)',
          backdropFilter: 'blur(10px)'
        }}>
          <div style={{ textAlign: 'center' }}>
            <label style={{
              display: 'block',
              marginBottom: 20,
              fontSize: 14,
              fontWeight: 700,
              color: '#f8fafc',
              textTransform: 'uppercase',
              letterSpacing: '1.5px',
              opacity: 0.9
            }}>
              Number of Clips
            </label>
            <select 
              value={numClips} 
              onChange={e => setNumClips(Number(e.target.value))}
              disabled={loading}
              style={{
                padding: '18px 28px',
                fontSize: 16,
                border: '2px solid rgba(148, 163, 184, 0.2)',
                borderRadius: 12,
                outline: 'none',
                backgroundColor: 'rgba(30, 41, 59, 0.8)',
                color: '#f8fafc',
                cursor: 'pointer',
                fontFamily: 'inherit',
                minWidth: 180,
                transition: 'all 0.3s ease',
                backdropFilter: 'blur(10px)',
                appearance: 'none',
                WebkitAppearance: 'none',
                MozAppearance: 'none',
                boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
              }}
            >
              <option value={1}>1 Clip</option>
              <option value={2}>2 Clips</option>
              <option value={3}>3 Clips</option>
              <option value={4}>4 Clips</option>
              <option value={5}>5 Clips</option>
            </select>
          </div>

          <div style={{ textAlign: 'center' }}>
            <label style={{
              display: 'block',
              marginBottom: 20,
              fontSize: 14,
              fontWeight: 700,
              color: '#f8fafc',
              textTransform: 'uppercase',
              letterSpacing: '1.5px',
              opacity: 0.9
            }}>
              Clip Duration
            </label>
            <select 
              value={clipDuration} 
              onChange={e => setClipDuration(Number(e.target.value))}
              disabled={loading}
              style={{
                padding: '18px 28px',
                fontSize: 16,
                border: '2px solid rgba(148, 163, 184, 0.2)',
                borderRadius: 12,
                outline: 'none',
                backgroundColor: 'rgba(30, 41, 59, 0.8)',
                color: '#f8fafc',
                cursor: 'pointer',
                fontFamily: 'inherit',
                minWidth: 200,
                transition: 'all 0.3s ease',
                backdropFilter: 'blur(10px)',
                appearance: 'none',
                WebkitAppearance: 'none',
                MozAppearance: 'none',
                boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
              }}
            >
              <option value={15}>15 seconds</option>
              <option value={30}>30 seconds</option>
              <option value={45}>45 seconds</option>
              <option value={60}>60 seconds</option>
            </select>
          </div>

          <button 
            onClick={handleAnalyze} 
            style={{ 
              display: 'inline-flex',
              alignItems: 'center',
              gap: 12,
              padding: '22px 52px', 
              fontSize: 18,
              fontWeight: 700,
              background: loading 
                ? 'rgba(148, 163, 184, 0.2)' 
                : useViralCaptions 
                  ? 'linear-gradient(135deg, #ef4444 0%, #3b82f6 100%)'
                  : 'linear-gradient(135deg, #3b82f6 0%, #8b5cf6 100%)',
              color: loading ? '#64748b' : '#ffffff',
              border: 'none',
              borderRadius: 16,
              cursor: loading ? 'not-allowed' : 'pointer',
              transition: 'all 0.3s ease',
              boxShadow: loading 
                ? 'none' 
                : useViralCaptions
                  ? '0 8px 32px rgba(239, 68, 68, 0.3), inset 0 1px 0 rgba(255, 255, 255, 0.1)'
                  : '0 8px 32px rgba(59, 130, 246, 0.3), inset 0 1px 0 rgba(255, 255, 255, 0.1)',
              transform: loading ? 'none' : 'translateY(0)',
              minWidth: 280,
              textTransform: 'uppercase',
              letterSpacing: '1.5px',
              position: 'relative',
              overflow: 'hidden'
            }}
            onMouseEnter={(e) => {
              if (!loading) {
                e.currentTarget.style.transform = 'translateY(-3px) scale(1.02)';
                e.currentTarget.style.boxShadow = useViralCaptions
                  ? '0 16px 40px rgba(239, 68, 68, 0.4), inset 0 1px 0 rgba(255, 255, 255, 0.1)'
                  : '0 16px 40px rgba(59, 130, 246, 0.4), inset 0 1px 0 rgba(255, 255, 255, 0.1)';
              }
            }}
            onMouseLeave={(e) => {
              if (!loading) {
                e.currentTarget.style.transform = 'translateY(0) scale(1)';
                e.currentTarget.style.boxShadow = useViralCaptions
                  ? '0 8px 32px rgba(239, 68, 68, 0.3), inset 0 1px 0 rgba(255, 255, 255, 0.1)'
                  : '0 8px 32px rgba(59, 130, 246, 0.3), inset 0 1px 0 rgba(255, 255, 255, 0.1)';
              }
            }}
            disabled={loading}
          >
            {loading ? (
              <>
                <LoadingIcon />
                Processing...
              </>
            ) : useViralCaptions ? (
              <>
                <RocketIcon />
                Generate VIRAL Clips
              </>
            ) : (
              <>
                <VideoIcon />
                Generate Clips
              </>
            )}
          </button>
        </div>

        {/* 🚀 VIRAL CAPTION SETTINGS */}
        <div style={{
          marginBottom: 56,
          padding: '48px',
          background: 'rgba(239, 68, 68, 0.08)',
          borderRadius: 20,
          border: '2px solid rgba(239, 68, 68, 0.2)',
          backdropFilter: 'blur(10px)'
        }}>
          <div style={{
            textAlign: 'center',
            marginBottom: 40
          }}>
            <div style={{
              display: 'inline-flex',
              alignItems: 'center',
              gap: 12,
              marginBottom: 20,
              padding: '12px 28px',
              background: 'rgba(239, 68, 68, 0.1)',
              borderRadius: 50,
              border: '1px solid rgba(239, 68, 68, 0.3)',
              backdropFilter: 'blur(10px)'
            }}>
              <RocketIcon />
              <span style={{ 
                fontSize: 14, 
                fontWeight: 600, 
                color: '#ef4444',
                textTransform: 'uppercase',
                letterSpacing: '2px'
              }}>
                Viral Caption System
              </span>
            </div>

            <h3 style={{
              fontSize: '2.25rem',
              fontWeight: 800,
              background: 'linear-gradient(135deg, #ef4444 0%, #3b82f6 100%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              margin: '0 0 16px 0',
              letterSpacing: '-0.02em'
            }}>
              Ultra-Engaging Captions
            </h3>
            <p style={{
              fontSize: '1.125rem',
              color: '#cbd5e1',
              margin: 0,
              opacity: 0.9,
              maxWidth: 500,
              marginLeft: 'auto',
              marginRight: 'auto'
            }}>
              Add TikTok/Instagram Reels style captions that keep viewers hooked
            </p>

            {/* Beta disclaimer */}
            <div style={{
              marginTop: 24,
              padding: '16px 24px',
              background: 'rgba(245, 158, 11, 0.1)',
              border: '1px solid rgba(245, 158, 11, 0.3)',
              borderRadius: 12,
              maxWidth: 600,
              marginLeft: 'auto',
              marginRight: 'auto'
            }}>
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: 12,
                marginBottom: 8
              }}>
                <svg width="20" height="20" viewBox="0 0 24 24" fill="#f59e0b">
                  <path d="M12 2L13.09 8.26L22 12L13.09 15.74L12 22L10.91 15.74L2 12L10.91 8.26L12 2Z"/>
                </svg>
                <span style={{
                  fontSize: 14,
                  fontWeight: 700,
                  color: '#f59e0b',
                  textTransform: 'uppercase',
                  letterSpacing: '1px'
                }}>
                  Beta Version
                </span>
              </div>
              <p style={{
                fontSize: 14,
                color: '#fbbf24',
                margin: 0,
                lineHeight: 1.5,
                opacity: 0.9
              }}>
                Viral captions are currently supported for <strong>English audio only</strong>. 
                Non-English videos will be processed without captions. We're working on 
                multi-language support for future releases.
              </p>
            </div>
          </div>

          {/* Viral Caption Toggle */}
          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            gap: 20,
            marginBottom: 40,
            padding: '32px',
            background: 'rgba(15, 23, 42, 0.3)',
            borderRadius: 16,
            border: '1px solid rgba(148, 163, 184, 0.1)'
          }}>
            <span style={{
              fontSize: 18,
              fontWeight: 600,
              color: '#f8fafc',
              textTransform: 'uppercase',
              letterSpacing: '1px'
            }}>
              Enable Viral Captions
            </span>
            <button
              onClick={() => setUseViralCaptions(!useViralCaptions)}
              style={{
                position: 'relative',
                width: 80,
                height: 40,
                borderRadius: 25,
                border: 'none',
                cursor: 'pointer',
                background: useViralCaptions 
                  ? 'linear-gradient(135deg, #ef4444 0%, #3b82f6 100%)'
                  : 'rgba(148, 163, 184, 0.3)',
                transition: 'all 0.3s ease',
                boxShadow: useViralCaptions 
                  ? '0 4px 16px rgba(239, 68, 68, 0.3), inset 0 1px 0 rgba(255, 255, 255, 0.1)'
                  : '0 2px 8px rgba(0, 0, 0, 0.1)',
                outline: 'none'
              }}
            >
              <div
                style={{
                  position: 'absolute',
                  top: 4,
                  left: useViralCaptions ? 44 : 4,
                  width: 32,
                  height: 32,
                  borderRadius: '50%',
                  background: '#ffffff',
                  transition: 'all 0.3s ease',
                  boxShadow: '0 2px 8px rgba(0, 0, 0, 0.2)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center'
                }}
              >
                {useViralCaptions ? (
                  <CheckIcon />
                ) : (
                  <CloseIcon />
                )}
              </div>
            </button>
            <span style={{
              fontSize: 16,
              fontWeight: 500,
              color: useViralCaptions ? '#3b82f6' : '#64748b'
            }}>
              {useViralCaptions ? 'ON' : 'OFF'}
            </span>
          </div>

          {/* Caption Settings (only show when viral captions are enabled) */}
          {useViralCaptions && (
            <div style={{
              display: 'flex',
              gap: 40,
              justifyContent: 'center',
              flexWrap: 'wrap'
            }}>
              {/* Caption Style Selection */}
              <div style={{ textAlign: 'center' }}>
                <label style={{
                  display: 'block',
                  marginBottom: 24,
                  fontSize: 14,
                  fontWeight: 700,
                  color: '#f8fafc',
                  textTransform: 'uppercase',
                  letterSpacing: '1.5px',
                  opacity: 0.9
                }}>
                  Caption Style
                </label>
                <div style={{ display: 'flex', gap: 16, flexDirection: 'column' }}>
                  <button
                    onClick={() => setCaptionStyle('single-word')}
                    style={{
                      padding: '20px 32px',
                      border: captionStyle === 'single-word' 
                        ? '2px solid #ef4444' 
                        : '2px solid rgba(148, 163, 184, 0.2)',
                      borderRadius: 12,
                      background: captionStyle === 'single-word'
                        ? 'rgba(239, 68, 68, 0.1)'
                        : 'rgba(30, 41, 59, 0.6)',
                      color: captionStyle === 'single-word' ? '#ef4444' : '#f8fafc',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      fontFamily: 'inherit',
                      fontSize: 16,
                      fontWeight: 600,
                      textAlign: 'left',
                      minWidth: 280,
                      boxShadow: captionStyle === 'single-word' 
                        ? '0 4px 16px rgba(239, 68, 68, 0.2)' 
                        : '0 2px 8px rgba(0, 0, 0, 0.1)',
                      backdropFilter: 'blur(10px)'
                    }}
                  >
                    <div style={{ display: 'flex', alignItems: 'center', gap: 12, marginBottom: 8 }}>
                      <RocketIcon />
                      <strong>Single Word (Ultra Viral)</strong>
                    </div>
                    <div style={{ fontSize: 14, opacity: 0.8, lineHeight: 1.4 }}>
                      One word per caption for maximum engagement and TikTok-style impact
                    </div>
                  </button>

                  <button
                    onClick={() => setCaptionStyle('engaging')}
                    style={{
                      padding: '20px 32px',
                      border: captionStyle === 'engaging' 
                        ? '2px solid #3b82f6' 
                        : '2px solid rgba(148, 163, 184, 0.2)',
                      borderRadius: 12,
                      background: captionStyle === 'engaging'
                        ? 'rgba(59, 130, 246, 0.1)'
                        : 'rgba(30, 41, 59, 0.6)',
                      color: captionStyle === 'engaging' ? '#3b82f6' : '#f8fafc',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      fontFamily: 'inherit',
                      fontSize: 16,
                      fontWeight: 600,
                      textAlign: 'left',
                      minWidth: 280,
                      boxShadow: captionStyle === 'engaging' 
                        ? '0 4px 16px rgba(59, 130, 246, 0.2)' 
                        : '0 2px 8px rgba(0, 0, 0, 0.1)',
                      backdropFilter: 'blur(10px)'
                    }}
                  >
                    <div style={{ display: 'flex', alignItems: 'center', gap: 12, marginBottom: 8 }}>
                      <SparkleIcon />
                      <strong>1-2 Words (Engaging)</strong>
                    </div>
                    <div style={{ fontSize: 14, opacity: 0.8, lineHeight: 1.4 }}>
                      Short phrases for balanced readability with viral engagement
                    </div>
                  </button>
                </div>
              </div>

              {/* Font Style Selection */}
              <div style={{ textAlign: 'center' }}>
                <label style={{
                  display: 'block',
                  marginBottom: 24,
                  fontSize: 14,
                  fontWeight: 700,
                  color: '#f8fafc',
                  textTransform: 'uppercase',
                  letterSpacing: '1.5px',
                  opacity: 0.9
                }}>
                  Font Style
                </label>
                <div style={{ display: 'flex', gap: 16, flexDirection: 'column' }}>
                  <button
                    onClick={() => setFontStyle('impact')}
                    style={{
                      padding: '20px 32px',
                      border: fontStyle === 'impact' 
                        ? '2px solid #8b5cf6' 
                        : '2px solid rgba(148, 163, 184, 0.2)',
                      borderRadius: 12,
                      background: fontStyle === 'impact'
                        ? 'rgba(139, 92, 246, 0.1)'
                        : 'rgba(30, 41, 59, 0.6)',
                      color: fontStyle === 'impact' ? '#8b5cf6' : '#f8fafc',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      fontFamily: 'inherit',
                      fontSize: 16,
                      fontWeight: 600,
                      textAlign: 'left',
                      minWidth: 280,
                      boxShadow: fontStyle === 'impact' 
                        ? '0 4px 16px rgba(139, 92, 246, 0.2)' 
                        : '0 2px 8px rgba(0, 0, 0, 0.1)',
                      backdropFilter: 'blur(10px)'
                    }}
                  >
                    <div style={{ display: 'flex', alignItems: 'center', gap: 12, marginBottom: 8 }}>
                      <strong style={{ fontFamily: 'Impact, Arial Black, sans-serif' }}>Impact (Bold & Sporty)</strong>
                    </div>
                    <div style={{ fontSize: 14, opacity: 0.8, lineHeight: 1.4 }}>
                      Bold, high-impact font perfect for fitness and action content
                    </div>
                  </button>

                  <button
                    onClick={() => setFontStyle('default')}
                    style={{
                      padding: '20px 32px',
                      border: fontStyle === 'default' 
                        ? '2px solid #10b981' 
                        : '2px solid rgba(148, 163, 184, 0.2)',
                      borderRadius: 12,
                      background: fontStyle === 'default'
                        ? 'rgba(16, 185, 129, 0.1)'
                        : 'rgba(30, 41, 59, 0.6)',
                      color: fontStyle === 'default' ? '#10b981' : '#f8fafc',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      fontFamily: 'inherit',
                      fontSize: 16,
                      fontWeight: 600,
                      textAlign: 'left',
                      minWidth: 280,
                      boxShadow: fontStyle === 'default' 
                        ? '0 4px 16px rgba(16, 185, 129, 0.2)' 
                        : '0 2px 8px rgba(0, 0, 0, 0.1)',
                      backdropFilter: 'blur(10px)'
                    }}
                  >
                    <div style={{ display: 'flex', alignItems: 'center', gap: 12, marginBottom: 8 }}>
                      <strong>Default (Clean & Modern)</strong>
                    </div>
                    <div style={{ fontSize: 14, opacity: 0.8, lineHeight: 1.4 }}>
                      Clean, modern font great for educational and lifestyle content
                    </div>
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* View all clips button */}
        <div style={{ 
          textAlign: 'center', 
          marginBottom: 56 
        }}>
          <button 
            onClick={fetchClipsList}
            style={{ 
              display: 'inline-flex',
              alignItems: 'center',
              gap: 12,
              padding: '18px 36px', 
              fontSize: 16,
              fontWeight: 600,
              background: 'rgba(148, 163, 184, 0.1)',
              color: '#f8fafc',
              border: '2px solid rgba(148, 163, 184, 0.2)',
              borderRadius: 12,
              cursor: 'pointer',
              transition: 'all 0.3s ease',
              backdropFilter: 'blur(10px)',
              boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
              textTransform: 'uppercase',
              letterSpacing: '1px'
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.transform = 'translateY(-2px)';
              e.currentTarget.style.boxShadow = '0 4px 16px rgba(148, 163, 184, 0.2)';
              e.currentTarget.style.borderColor = '#64748b';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = 'translateY(0)';
              e.currentTarget.style.boxShadow = '0 2px 8px rgba(0, 0, 0, 0.1)';
              e.currentTarget.style.borderColor = 'rgba(148, 163, 184, 0.2)';
            }}
          >
            <FolderIcon />
            View All Existing Clips
          </button>
        </div>

        {/* Enhanced status section */}
        {status && (
          <div style={{
            padding: '24px 32px',
            margin: '32px 0',
            borderRadius: 16,
            background: status.includes('Error') || status.includes('error')
              ? 'rgba(239, 68, 68, 0.1)'
              : status.includes('completed') || status.includes('SUCCESS') || status.includes('created') || status.includes('added')
                ? 'rgba(16, 185, 129, 0.1)'
                : 'rgba(59, 130, 246, 0.1)',
            border: `2px solid ${
              status.includes('Error') || status.includes('error')
                ? 'rgba(239, 68, 68, 0.3)'
                : status.includes('completed') || status.includes('SUCCESS') || status.includes('created') || status.includes('added')
                  ? 'rgba(16, 185, 129, 0.3)'
                  : 'rgba(59, 130, 246, 0.3)'
            }`,
            color: status.includes('Error') || status.includes('error')
              ? '#ef4444'
              : status.includes('completed') || status.includes('SUCCESS') || status.includes('created') || status.includes('added')
                ? '#10b981'
                : '#3b82f6',
            fontSize: 16,
            fontWeight: 500,
            textAlign: 'center',
            backdropFilter: 'blur(10px)',
            boxShadow: '0 4px 16px rgba(0, 0, 0, 0.1)'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 12 }}>
              {status.includes('Error') || status.includes('error') ? (
                <CloseIcon />
              ) : status.includes('completed') || status.includes('SUCCESS') || status.includes('created') || status.includes('added') ? (
                <CheckIcon />
              ) : (
                <LoadingIcon />
              )}
              <span>{status}</span>
            </div>
          </div>
        )}

        {/* Enhanced clips display */}
        {clips.length > 0 && (
          <div style={{
            marginTop: 56,
            padding: '48px',
            background: 'rgba(15, 23, 42, 0.4)',
            borderRadius: 20,
            border: '1px solid rgba(148, 163, 184, 0.1)',
            backdropFilter: 'blur(10px)'
          }}>
            <div style={{
              textAlign: 'center',
              marginBottom: 40
            }}>
              <h3 style={{
                fontSize: '2rem',
                fontWeight: 800,
                background: 'linear-gradient(135deg, #3b82f6 0%, #8b5cf6 100%)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                margin: '0 0 16px 0',
                letterSpacing: '-0.02em'
              }}>
                Generated Clips ({clips.length})
              </h3>
              <p style={{
                fontSize: '1.125rem',
                color: '#cbd5e1',
                margin: 0,
                opacity: 0.9
              }}>
                Your viral-ready video clips with AI-powered captions
              </p>
            </div>

            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(400px, 1fr))',
              gap: 32,
              maxHeight: '70vh',
              overflowY: 'auto',
              padding: '8px'
            }}>
              {clips.map((clip, index) => (
                <div key={clip.id || index} style={{
                  background: 'rgba(30, 41, 59, 0.8)',
                  borderRadius: 16,
                  padding: '32px',
                  border: '1px solid rgba(148, 163, 184, 0.1)',
                  boxShadow: '0 8px 32px rgba(0, 0, 0, 0.2)',
                  backdropFilter: 'blur(10px)',
                  transition: 'all 0.3s ease'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = 'translateY(-4px)';
                  e.currentTarget.style.boxShadow = '0 16px 40px rgba(0, 0, 0, 0.3)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'translateY(0)';
                  e.currentTarget.style.boxShadow = '0 8px 32px rgba(0, 0, 0, 0.2)';
                }}
                >
                  <div style={{ marginBottom: 24 }}>
                    <div style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: 12,
                      marginBottom: 16
                    }}>
                      {clip.isViral ? <RocketIcon /> : <VideoIcon />}
                      <h4 style={{
                        fontSize: 18,
                        fontWeight: 700,
                        color: '#f8fafc',
                        margin: 0,
                        flex: 1
                      }}>
                        Clip {index + 1}
                        {clip.isViral && (
                          <span style={{
                            fontSize: 12,
                            fontWeight: 600,
                            color: '#ef4444',
                            marginLeft: 8,
                            padding: '4px 8px',
                            background: 'rgba(239, 68, 68, 0.1)',
                            borderRadius: 6,
                            textTransform: 'uppercase',
                            letterSpacing: '1px'
                          }}>
                            VIRAL
                          </span>
                        )}
                      </h4>
                    </div>

                    <p style={{
                      fontSize: 14,
                      color: '#cbd5e1',
                      margin: '0 0 20px 0',
                      opacity: 0.8
                    }}>
                      {clip.description}
                    </p>

                    <div style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: 16,
                      marginBottom: 20,
                      fontSize: 14,
                      color: '#94a3b8'
                    }}>
                      <div style={{ display: 'flex', alignItems: 'center', gap: 6 }}>
                        <ClockIcon />
                        <span>{clip.start_time} - {clip.end_time}</span>
                      </div>
                      <span>•</span>
                      <span>{clip.duration}s</span>
                      {clip.fileSize && (
                        <>
                          <span>•</span>
                          <span>{(clip.fileSize / (1024 * 1024)).toFixed(1)}MB</span>
                        </>
                      )}
                    </div>

                    {clip.captionStyle && (
                      <div style={{
                        fontSize: 12,
                        color: '#3b82f6',
                        fontWeight: 600,
                        textTransform: 'uppercase',
                        letterSpacing: '1px',
                        marginBottom: 20
                      }}>
                        {clip.captionStyle} captions • {clip.fontStyle || 'default'} font
                      </div>
                    )}
                  </div>

                  {/* Video Preview */}
                                     <video 
                     controls 
                     style={{
                       width: '100%',
                       maxHeight: 280,
                       borderRadius: 12,
                       marginBottom: 24,
                       boxShadow: '0 8px 24px rgba(0, 0, 0, 0.4)',
                       backgroundColor: '#000000',
                       border: '1px solid rgba(148, 163, 184, 0.2)'
                     }}
                     src={`http://localhost:5000/clips/${clip.filename}`}
                   />

                                     <div style={{
                     display: 'flex',
                     justifyContent: 'center'
                   }}>
                     <a 
                       href={`http://localhost:5000/clips/${clip.filename}`}
                       download={clip.filename}
                       style={{
                         display: 'inline-flex',
                         alignItems: 'center',
                         gap: 8,
                         padding: '12px 24px',
                         fontSize: 14,
                         fontWeight: 600,
                         background: 'linear-gradient(135deg, #3b82f6 0%, #8b5cf6 100%)',
                         color: '#ffffff',
                         textDecoration: 'none',
                         borderRadius: 10,
                         cursor: 'pointer',
                         transition: 'all 0.3s ease',
                         boxShadow: '0 4px 16px rgba(59, 130, 246, 0.3)',
                         justifyContent: 'center',
                         textTransform: 'uppercase',
                         letterSpacing: '1px',
                         minWidth: 160
                       }}
                      onMouseEnter={(e) => {
                        e.currentTarget.style.transform = 'translateY(-2px)';
                        e.currentTarget.style.boxShadow = '0 8px 24px rgba(59, 130, 246, 0.4)';
                      }}
                      onMouseLeave={(e) => {
                        e.currentTarget.style.transform = 'translateY(0)';
                        e.currentTarget.style.boxShadow = '0 4px 16px rgba(59, 130, 246, 0.3)';
                      }}
                    >
                      <DownloadIcon />
                      Download
                    </a>

                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
