import React, { useState } from 'react';
import './App.css';

interface Clip {
  id: number;
  title: string;
  description: string;
  start_time: string;
  end_time: string;
  duration: number;
  timestamp: number;
  filename: string;
}

function App() {
  const [ytLink, setYtLink] = useState('');
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState('');
  const [clips, setClips] = useState<Clip[]>([]);
  const [numClips, setNumClips] = useState(3);
  const [clipDuration, setClipDuration] = useState(30);

  const handleAnalyze = async () => {
    if (!ytLink) {
      setStatus('Please enter a YouTube link');
      return;
    }

    setLoading(true);
    setStatus('Processing video...');
    setClips([]);

    try {
      const response = await fetch('http://localhost:5000/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          ytLink,
          numClips,
          clipDuration
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setStatus('Viral clip analysis completed! Use the timestamps to create your clips.');
        setClips(data.clips || []);
      } else {
        setStatus(`Error: ${data.error}`);
      }
    } catch (error) {
      setStatus('Error connecting to server');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App" style={{ 
      minHeight: '100vh',
      background: 'radial-gradient(ellipse at top, #0a0a0a 0%, #000000 50%, #0a0a0a 100%)',
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
      padding: '40px 20px',
      color: '#ffffff',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Animated background elements */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'radial-gradient(circle at 20% 80%, rgba(0, 255, 136, 0.03) 0%, transparent 50%), radial-gradient(circle at 80% 20%, rgba(0, 204, 255, 0.03) 0%, transparent 50%), radial-gradient(circle at 40% 40%, rgba(255, 0, 128, 0.02) 0%, transparent 50%)',
        pointerEvents: 'none'
      }} />

      <div style={{
        maxWidth: 1200,
        margin: '0 auto',
        background: 'rgba(17, 17, 17, 0.8)',
        borderRadius: 32,
        padding: 56,
        boxShadow: '0 32px 64px -12px rgba(0, 0, 0, 0.9), inset 0 1px 0 rgba(255, 255, 255, 0.1)',
        border: '1px solid rgba(255, 255, 255, 0.1)',
        backdropFilter: 'blur(20px)',
        position: 'relative'
      }}>
        {/* Header with premium styling */}
        <div style={{ textAlign: 'center', marginBottom: 56 }}>
          <div style={{
            display: 'inline-flex',
            alignItems: 'center',
            gap: 16,
            marginBottom: 24,
            padding: '12px 24px',
            background: 'rgba(0, 255, 136, 0.1)',
            borderRadius: 50,
            border: '1px solid rgba(0, 255, 136, 0.2)',
            backdropFilter: 'blur(10px)'
          }}>
            <span style={{ fontSize: 24 }}>‚ú®</span>
            <span style={{ 
              fontSize: 14, 
              fontWeight: 600, 
              color: '#00ff88',
              textTransform: 'uppercase',
              letterSpacing: '2px'
            }}>
              AI-Powered
            </span>
          </div>

          <h1 style={{
            fontSize: '3.5rem',
            fontWeight: 900,
            background: 'linear-gradient(135deg, #ffffff 0%, #00ff88 25%, #00ccff 50%, #ff0080 75%, #ffffff 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            margin: '0 0 20px 0',
            letterSpacing: '-0.04em',
            textShadow: '0 0 40px rgba(0, 255, 136, 0.3)',
            lineHeight: 1.1
          }}>
            Viral Clip Generator
          </h1>
          <p style={{
            fontSize: '1.3rem',
            color: '#cccccc',
            margin: 0,
            fontWeight: 400,
            opacity: 0.8,
            maxWidth: 600,
            marginLeft: 'auto',
            marginRight: 'auto',
            lineHeight: 1.6
          }}>
            Transform any YouTube video into viral social media clips with AI-powered analysis
          </p>
        </div>

        {/* Enhanced input section */}
        <div style={{ marginBottom: 48 }}>
          <div style={{
            position: 'relative',
            maxWidth: 800,
            margin: '0 auto'
          }}>
            <div style={{
              position: 'absolute',
              top: -8,
              left: 24,
              background: '#111111',
              padding: '0 12px',
              fontSize: 14,
              fontWeight: 600,
              color: '#00ff88',
              textTransform: 'uppercase',
              letterSpacing: '1px',
              zIndex: 1
            }}>
              YouTube URL
            </div>
            <input
              type="text"
              placeholder="https://www.youtube.com/watch?v=..."
              value={ytLink}
              onChange={e => setYtLink(e.target.value)}
              style={{
                width: '100%',
                padding: '24px 28px',
                fontSize: 18,
                border: '2px solid rgba(255, 255, 255, 0.1)',
                borderRadius: 20,
                outline: 'none',
                transition: 'all 0.4s ease',
                fontFamily: 'inherit',
                backgroundColor: 'rgba(0, 0, 0, 0.3)',
                color: '#ffffff',
                boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3), inset 0 1px 0 rgba(255, 255, 255, 0.1)',
                backdropFilter: 'blur(10px)'
              }}
              onFocus={(e) => {
                e.target.style.borderColor = '#00ff88';
                e.target.style.boxShadow = '0 0 0 4px rgba(0, 255, 136, 0.2), 0 8px 32px rgba(0, 0, 0, 0.3), inset 0 1px 0 rgba(255, 255, 255, 0.1)';
                e.target.style.transform = 'translateY(-2px)';
              }}
              onBlur={(e) => {
                e.target.style.borderColor = 'rgba(255, 255, 255, 0.1)';
                e.target.style.boxShadow = '0 8px 32px rgba(0, 0, 0, 0.3), inset 0 1px 0 rgba(255, 255, 255, 0.1)';
                e.target.style.transform = 'translateY(0)';
              }}
              disabled={loading}
            />
          </div>
        </div>

        {/* Enhanced controls section */}
        <div style={{
          display: 'flex',
          gap: 40,
          alignItems: 'center',
          justifyContent: 'center',
          flexWrap: 'wrap',
          marginBottom: 48,
          padding: '32px',
          background: 'rgba(0, 0, 0, 0.2)',
          borderRadius: 24,
          border: '1px solid rgba(255, 255, 255, 0.05)',
          backdropFilter: 'blur(10px)'
        }}>
          <div style={{ textAlign: 'center' }}>
            <label style={{
              display: 'block',
              marginBottom: 16,
              fontSize: 14,
              fontWeight: 700,
              color: '#ffffff',
              textTransform: 'uppercase',
              letterSpacing: '1.5px',
              opacity: 0.9
            }}>
              Number of Clips
            </label>
            <select 
              value={numClips} 
              onChange={e => setNumClips(Number(e.target.value))}
              disabled={loading}
              style={{
                padding: '16px 24px',
                fontSize: 16,
                border: '2px solid rgba(255, 255, 255, 0.1)',
                borderRadius: 16,
                outline: 'none',
                backgroundColor: 'rgba(20, 20, 20, 0.95)',
                color: '#fff',
                cursor: 'pointer',
                fontFamily: 'inherit',
                minWidth: 160,
                transition: 'all 0.3s ease',
                backdropFilter: 'blur(10px)',
                appearance: 'none',
                WebkitAppearance: 'none',
                MozAppearance: 'none',
                boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
              }}
            >
              <option value={1}>1 Clip</option>
              <option value={2}>2 Clips</option>
              <option value={3}>3 Clips</option>
              <option value={4}>4 Clips</option>
              <option value={5}>5 Clips</option>
            </select>
          </div>

          <div style={{ textAlign: 'center' }}>
            <label style={{
              display: 'block',
              marginBottom: 16,
              fontSize: 14,
              fontWeight: 700,
              color: '#ffffff',
              textTransform: 'uppercase',
              letterSpacing: '1.5px',
              opacity: 0.9
            }}>
              Clip Duration
            </label>
            <select 
              value={clipDuration} 
              onChange={e => setClipDuration(Number(e.target.value))}
              disabled={loading}
              style={{
                padding: '16px 24px',
                fontSize: 16,
                border: '2px solid rgba(255, 255, 255, 0.1)',
                borderRadius: 16,
                outline: 'none',
                backgroundColor: 'rgba(20, 20, 20, 0.95)',
                color: '#fff',
                cursor: 'pointer',
                fontFamily: 'inherit',
                minWidth: 180,
                transition: 'all 0.3s ease',
                backdropFilter: 'blur(10px)',
                appearance: 'none',
                WebkitAppearance: 'none',
                MozAppearance: 'none',
                boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
              }}
            >
              <option value={15}>15 seconds</option>
              <option value={30}>30 seconds</option>
              <option value={45}>45 seconds</option>
              <option value={60}>60 seconds</option>
            </select>
          </div>

          <button 
            onClick={handleAnalyze} 
            style={{ 
              padding: '20px 48px', 
              fontSize: 18,
              fontWeight: 700,
              background: loading 
                ? 'rgba(255, 255, 255, 0.1)' 
                : 'linear-gradient(135deg, #00ff88 0%, #00ccff 100%)',
              color: loading ? '#666666' : '#000000',
              border: 'none',
              borderRadius: 20,
              cursor: loading ? 'not-allowed' : 'pointer',
              transition: 'all 0.4s ease',
              boxShadow: loading 
                ? 'none' 
                : '0 12px 40px rgba(0, 255, 136, 0.4), inset 0 1px 0 rgba(255, 255, 255, 0.2)',
              transform: loading ? 'none' : 'translateY(0)',
              minWidth: 260,
              textTransform: 'uppercase',
              letterSpacing: '1.5px',
              position: 'relative',
              overflow: 'hidden'
            }}
            onMouseEnter={(e) => {
              if (!loading) {
                e.currentTarget.style.transform = 'translateY(-4px) scale(1.02)';
                e.currentTarget.style.boxShadow = '0 20px 50px rgba(0, 255, 136, 0.5), inset 0 1px 0 rgba(255, 255, 255, 0.2)';
              }
            }}
            onMouseLeave={(e) => {
              if (!loading) {
                e.currentTarget.style.transform = 'translateY(0) scale(1)';
                e.currentTarget.style.boxShadow = '0 12px 40px rgba(0, 255, 136, 0.4), inset 0 1px 0 rgba(255, 255, 255, 0.2)';
              }
            }}
            disabled={loading}
          >
            {loading ? '‚è≥ Processing...' : 'üöÄ Generate Viral Clips'}
          </button>
        </div>

        {/* Enhanced status section */}
        <div style={{ marginTop: 40, minHeight: 40, textAlign: 'center' }}>
          {status && (
            <div style={{
              padding: '20px 32px',
              borderRadius: 16,
              fontSize: 16,
              fontWeight: 600,
              backgroundColor: status.includes('Error') ? 'rgba(255, 0, 0, 0.1)' : 'rgba(0, 255, 136, 0.1)',
              color: status.includes('Error') ? '#ff6666' : '#00ff88',
              border: `2px solid ${status.includes('Error') ? 'rgba(255, 0, 0, 0.2)' : 'rgba(0, 255, 136, 0.2)'}`,
              backdropFilter: 'blur(20px)',
              display: 'inline-block',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
            }}>
              {status}
            </div>
          )}
        </div>

        {/* Enhanced clips section */}
        {clips.length > 0 && (
          <div style={{ marginTop: 64 }}>
            <div style={{
              textAlign: 'center',
              marginBottom: 48,
              position: 'relative'
            }}>
              <h2 style={{
                fontSize: '2.5rem',
                fontWeight: 800,
                color: '#ffffff',
                margin: '0 0 16px 0',
                textTransform: 'uppercase',
                letterSpacing: '2px'
              }}>
                üéØ Your Viral Clips
              </h2>
              <div style={{
                display: 'inline-flex',
                alignItems: 'center',
                gap: 12,
                padding: '8px 20px',
                background: 'rgba(0, 255, 136, 0.1)',
                borderRadius: 50,
                border: '1px solid rgba(0, 255, 136, 0.2)',
                fontSize: 14,
                fontWeight: 600,
                color: '#00ff88',
                textTransform: 'uppercase',
                letterSpacing: '1px'
              }}>
                <span>‚ú®</span>
                {clips.length} Clips Generated
              </div>
            </div>
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(400px, 1fr))',
              gap: 40
            }}>
              {clips.map((clip, index) => (
                <div key={index} style={{ 
                  background: 'rgba(0, 0, 0, 0.4)',
                  border: '1px solid rgba(255, 255, 255, 0.1)',
                  borderRadius: 24,
                  padding: 40,
                  boxShadow: '0 16px 48px rgba(0, 0, 0, 0.4), inset 0 1px 0 rgba(255, 255, 255, 0.1)',
                  transition: 'all 0.5s ease',
                  position: 'relative',
                  overflow: 'hidden',
                  backdropFilter: 'blur(20px)'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = 'translateY(-12px) scale(1.02)';
                  e.currentTarget.style.boxShadow = '0 32px 64px rgba(0, 0, 0, 0.6), inset 0 1px 0 rgba(255, 255, 255, 0.1)';
                  e.currentTarget.style.borderColor = 'rgba(0, 255, 136, 0.3)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'translateY(0) scale(1)';
                  e.currentTarget.style.boxShadow = '0 16px 48px rgba(0, 0, 0, 0.4), inset 0 1px 0 rgba(255, 255, 255, 0.1)';
                  e.currentTarget.style.borderColor = 'rgba(255, 255, 255, 0.1)';
                }}>
                  {/* Gradient accent line */}
                  <div style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    height: 8,
                    background: 'linear-gradient(90deg, #00ff88 0%, #00ccff 50%, #ff0080 100%)',
                    borderRadius: '24px 24px 0 0'
                  }} />

                  {/* Clip number badge */}
                  <div style={{
                    position: 'absolute',
                    top: 20,
                    right: 20,
                    background: 'rgba(0, 255, 136, 0.1)',
                    color: '#00ff88',
                    padding: '8px 16px',
                    borderRadius: 20,
                    fontSize: 12,
                    fontWeight: 700,
                    textTransform: 'uppercase',
                    letterSpacing: '1px',
                    border: '1px solid rgba(0, 255, 136, 0.2)'
                  }}>
                    Clip {index + 1}
                  </div>

                  <h3 style={{
                    margin: '0 0 20px 0',
                    fontSize: '1.4rem',
                    fontWeight: 700,
                    color: '#ffffff',
                    lineHeight: 1.4,
                    textTransform: 'uppercase',
                    letterSpacing: '0.5px',
                    paddingRight: 80
                  }}>
                    {clip.title}
                  </h3>

                  <div style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: 8,
                    marginBottom: 24,
                    padding: '12px 16px',
                    background: 'rgba(0, 255, 136, 0.05)',
                    borderRadius: 12,
                    border: '1px solid rgba(0, 255, 136, 0.1)'
                  }}>
                    <span style={{ fontSize: 16 }}>‚è±Ô∏è</span>
                    <span style={{
                      fontSize: 14,
                      color: '#00ff88',
                      fontWeight: 600,
                      textTransform: 'uppercase',
                      letterSpacing: '1px'
                    }}>
                      {clip.start_time} - {clip.end_time}
                    </span>
                  </div>

                  <p style={{
                    margin: '0 0 28px 0',
                    fontSize: 15,
                    color: '#cccccc',
                    lineHeight: 1.7,
                    fontWeight: 400,
                    opacity: 0.9
                  }}>
                    {clip.description}
                  </p>

                  <video 
                    controls 
                    style={{
                      width: '100%',
                      maxHeight: 240,
                      borderRadius: 16,
                      marginBottom: 24,
                      boxShadow: '0 8px 32px rgba(0, 0, 0, 0.6)',
                      backgroundColor: '#000000',
                      border: '1px solid rgba(255, 255, 255, 0.1)'
                    }}
                    src={`http://localhost:5000/clips/${clip.filename}`}
                  />

                  <a
                    href={`http://localhost:5000/download/${clip.filename}`}
                    download
                    style={{
                      display: 'inline-flex',
                      alignItems: 'center',
                      gap: 12,
                      padding: '18px 32px',
                      backgroundColor: 'rgba(255, 0, 128, 0.1)',
                      color: '#ff0080',
                      border: '2px solid rgba(255, 0, 128, 0.3)',
                      borderRadius: 16,
                      cursor: 'pointer',
                      fontSize: 16,
                      fontWeight: 700,
                      textDecoration: 'none',
                      transition: 'all 0.4s ease',
                      textTransform: 'uppercase',
                      letterSpacing: '1.5px',
                      backdropFilter: 'blur(10px)'
                    }}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.backgroundColor = 'rgba(255, 0, 128, 0.2)';
                      e.currentTarget.style.transform = 'translateY(-3px)';
                      e.currentTarget.style.boxShadow = '0 12px 32px rgba(255, 0, 128, 0.3)';
                      e.currentTarget.style.borderColor = 'rgba(255, 0, 128, 0.5)';
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.backgroundColor = 'rgba(255, 0, 128, 0.1)';
                      e.currentTarget.style.transform = 'translateY(0)';
                      e.currentTarget.style.boxShadow = 'none';
                      e.currentTarget.style.borderColor = 'rgba(255, 0, 128, 0.3)';
                    }}
                  >
                    üì• Download Clip
                  </a>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
