import React, { useState } from 'react';
import './App.css';

interface Clip {
  id: number;
  title: string;
  description: string;
  start_time: string;
  end_time: string;
  duration: number;
  timestamp: number;
  filename: string;
  captionStyle?: string;
  fontStyle?: string;
  isViral?: boolean;
  fileSize?: number;
}

// SVG Icons as components
const SparkleIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M12 0l3.09 6.26L22 9.27l-5.45 5.32L18.18 24 12 20.27 5.82 24l1.63-9.41L2 9.27l6.91-3.01L12 0z"/>
  </svg>
);

const RocketIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M12 2L13.09 8.26L22 12L13.09 15.74L12 22L10.91 15.74L2 12L10.91 8.26L12 2Z"/>
  </svg>
);

const VideoIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M8 5v14l11-7z"/>
  </svg>
);

const ClockIcon = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
    <circle cx="12" cy="12" r="10"/>
    <polyline points="12,6 12,12 16,14"/>
  </svg>
);

const DownloadIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"/>
    <polyline points="7,10 12,15 17,10"/>
    <line x1="12" y1="15" x2="12" y2="3"/>
  </svg>
);

const FolderIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <path d="M22 19a2 2 0 0 1-2 2H4a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h5l2 3h9a2 2 0 0 1 2 2z"/>
  </svg>
);

const LoadingIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor" className="animate-spin">
    <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" strokeDasharray="32" strokeDashoffset="32">
      <animate attributeName="stroke-dashoffset" dur="1s" values="32;0" repeatCount="indefinite"/>
    </circle>
  </svg>
);

const CheckIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <polyline points="20,6 9,17 4,12"/>
  </svg>
);

const CloseIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <line x1="18" y1="6" x2="6" y2="18"/>
    <line x1="6" y1="6" x2="18" y2="18"/>
  </svg>
);

function App() {
  const [ytLink, setYtLink] = useState('');
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState('');
  const [clips, setClips] = useState<Clip[]>([]);
  const [numClips, setNumClips] = useState(3);
  const [clipDuration, setClipDuration] = useState(30);
  const [useViralCaptions, setUseViralCaptions] = useState(true);
  const [captionStyle, setCaptionStyle] = useState('single-word');
  const [fontStyle, setFontStyle] = useState('impact');
  const [processingViral, setProcessingViral] = useState(false);

  const handleAnalyze = async () => {
    if (!ytLink) {
      setStatus('Please enter a YouTube link');
      return;
    }

    setLoading(true);
    setStatus('Processing video...');
    setClips([]);

    try {
      // Choose endpoint based on viral caption setting
      const endpoint = useViralCaptions ? 
        'http://localhost:5000/analyze-viral' : 
        'http://localhost:5000/analyze';

      const requestBody = useViralCaptions ? {
        ytLink,
        numClips,
        clipDuration,
        captionStyle,
        fontStyle
      } : {
        ytLink,
        numClips,
        clipDuration
      };

      const response = await fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });

      const data = await response.json();

      if (response.ok) {
        if (useViralCaptions) {
          setStatus(`${data.clips?.length || 0} viral clips created with ${captionStyle} captions!`);
        } else {
          setStatus('Viral clip analysis completed! Use the timestamps to create your clips.');
        }
        setClips(data.clips || []);
      } else {
        setStatus(`Error: ${data.error}`);
      }
    } catch (error) {
      setStatus('Error connecting to server');
    } finally {
      setLoading(false);
    }
  };

  const addViralCaptions = async (clipFilename: string) => {
    setProcessingViral(true);
    setStatus(`Adding ${captionStyle} viral captions to clip...`);

    try {
      const response = await fetch('http://localhost:5000/add-viral-captions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          clipFilename,
          captionStyle,
          fontStyle
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setStatus(`Viral captions added! File: ${data.viralFilename}`);
        // Refresh clips list to show the new viral version
        fetchClipsList();
      } else {
        setStatus(`Error: ${data.error}`);
      }
    } catch (error) {
      setStatus('Error adding viral captions');
    } finally {
      setProcessingViral(false);
    }
  };

  const fetchClipsList = async () => {
    try {
      const response = await fetch('http://localhost:5000/clips-list');
      const data = await response.json();

      if (response.ok) {
        // Convert the clips format to match our interface
        const formattedClips = data.clips.map((clip: any, index: number) => ({
          id: index,
          filename: clip.filename,
          title: clip.filename.includes('VIRAL_') ? 
            `VIRAL ${clip.filename}` : 
            clip.filename,
          description: clip.isViral ? 
            `Viral ${clip.captionStyle} captions` : 
            'Standard clip',
          start_time: '0:00',
          end_time: '0:30',
          duration: 30,
          timestamp: new Date(clip.created).getTime(),
          captionStyle: clip.captionStyle,
          isViral: clip.isViral,
          fileSize: clip.size
        }));

        setClips(formattedClips);
      }
    } catch (error) {
      console.error('Error fetching clips:', error);
    }
  };

  return (
    <div className="App" style={{ 
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0f172a 0%, #1e293b 50%, #0f172a 100%)',
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
      padding: '48px 24px',
      color: '#f8fafc',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Animated background elements */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'radial-gradient(circle at 20% 80%, rgba(59, 130, 246, 0.05) 0%, transparent 50%), radial-gradient(circle at 80% 20%, rgba(147, 51, 234, 0.05) 0%, transparent 50%), radial-gradient(circle at 40% 40%, rgba(239, 68, 68, 0.03) 0%, transparent 50%)',
        pointerEvents: 'none'
      }} />

      <div style={{
        maxWidth: 1200,
        margin: '0 auto',
        background: 'rgba(30, 41, 59, 0.8)',
        borderRadius: 24,
        padding: 64,
        boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.8), inset 0 1px 0 rgba(255, 255, 255, 0.1)',
        border: '1px solid rgba(148, 163, 184, 0.1)',
        backdropFilter: 'blur(20px)',
        position: 'relative'
      }}>
        {/* Header with premium styling */}
        <div style={{ textAlign: 'center', marginBottom: 64 }}>
          <div style={{
            display: 'inline-flex',
            alignItems: 'center',
            gap: 12,
            marginBottom: 32,
            padding: '12px 24px',
            background: 'rgba(59, 130, 246, 0.1)',
            borderRadius: 50,
            border: '1px solid rgba(59, 130, 246, 0.2)',
            backdropFilter: 'blur(10px)'
          }}>
            <SparkleIcon />
            <span style={{ 
              fontSize: 14, 
              fontWeight: 600, 
              color: '#3b82f6',
              textTransform: 'uppercase',
              letterSpacing: '2px'
            }}>
              AI-Powered
            </span>
          </div>

          <h1 style={{
            fontSize: '4rem',
            fontWeight: 900,
            background: 'linear-gradient(135deg, #f8fafc 0%, #3b82f6 25%, #8b5cf6 50%, #ef4444 75%, #f8fafc 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            margin: '0 0 24px 0',
            letterSpacing: '-0.04em',
            lineHeight: 1.1
          }}>
            Viral Clip Generator
          </h1>
          <p style={{
            fontSize: '1.25rem',
            color: '#cbd5e1',
            margin: 0,
            fontWeight: 400,
            opacity: 0.9,
            maxWidth: 600,
            marginLeft: 'auto',
            marginRight: 'auto',
            lineHeight: 1.6
          }}>
            Transform any YouTube video into viral social media clips with AI-powered analysis
          </p>
        </div>

        {/* Enhanced input section */}
        <div style={{ marginBottom: 48 }}>
          <div style={{
            position: 'relative',
            maxWidth: 800,
            margin: '0 auto'
          }}>
            <div style={{
              position: 'absolute',
              top: -8,
              left: 24,
              background: '#111111',
              padding: '0 12px',
              fontSize: 14,
              fontWeight: 600,
              color: '#00ff88',
              textTransform: 'uppercase',
              letterSpacing: '1px',
              zIndex: 1
            }}>
              YouTube URL
            </div>
            <input
              type="text"
              placeholder="https://www.youtube.com/watch?v=..."
              value={ytLink}
              onChange={e => setYtLink(e.target.value)}
              style={{
                width: '100%',
                padding: '24px 28px',
                fontSize: 18,
                border: '2px solid rgba(255, 255, 255, 0.1)',
                borderRadius: 20,
                outline: 'none',
                transition: 'all 0.4s ease',
                fontFamily: 'inherit',
                backgroundColor: 'rgba(0, 0, 0, 0.3)',
                color: '#ffffff',
                boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3), inset 0 1px 0 rgba(255, 255, 255, 0.1)',
                backdropFilter: 'blur(10px)'
              }}
              onFocus={(e) => {
                e.target.style.borderColor = '#00ff88';
                e.target.style.boxShadow = '0 0 0 4px rgba(0, 255, 136, 0.2), 0 8px 32px rgba(0, 0, 0, 0.3), inset 0 1px 0 rgba(255, 255, 255, 0.1)';
                e.target.style.transform = 'translateY(-2px)';
              }}
              onBlur={(e) => {
                e.target.style.borderColor = 'rgba(255, 255, 255, 0.1)';
                e.target.style.boxShadow = '0 8px 32px rgba(0, 0, 0, 0.3), inset 0 1px 0 rgba(255, 255, 255, 0.1)';
                e.target.style.transform = 'translateY(0)';
              }}
              disabled={loading}
            />
          </div>
        </div>

        {/* Enhanced controls section */}
        <div style={{
          display: 'flex',
          gap: 40,
          alignItems: 'center',
          justifyContent: 'center',
          flexWrap: 'wrap',
          marginBottom: 48,
          padding: '32px',
          background: 'rgba(0, 0, 0, 0.2)',
          borderRadius: 24,
          border: '1px solid rgba(255, 255, 255, 0.05)',
          backdropFilter: 'blur(10px)'
        }}>
          <div style={{ textAlign: 'center' }}>
            <label style={{
              display: 'block',
              marginBottom: 16,
              fontSize: 14,
              fontWeight: 700,
              color: '#ffffff',
              textTransform: 'uppercase',
              letterSpacing: '1.5px',
              opacity: 0.9
            }}>
              Number of Clips
            </label>
            <select 
              value={numClips} 
              onChange={e => setNumClips(Number(e.target.value))}
              disabled={loading}
              style={{
                padding: '16px 24px',
                fontSize: 16,
                border: '2px solid rgba(255, 255, 255, 0.1)',
                borderRadius: 16,
                outline: 'none',
                backgroundColor: 'rgba(20, 20, 20, 0.95)',
                color: '#fff',
                cursor: 'pointer',
                fontFamily: 'inherit',
                minWidth: 160,
                transition: 'all 0.3s ease',
                backdropFilter: 'blur(10px)',
                appearance: 'none',
                WebkitAppearance: 'none',
                MozAppearance: 'none',
                boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
              }}
            >
              <option value={1}>1 Clip</option>
              <option value={2}>2 Clips</option>
              <option value={3}>3 Clips</option>
              <option value={4}>4 Clips</option>
              <option value={5}>5 Clips</option>
            </select>
          </div>

          <div style={{ textAlign: 'center' }}>
            <label style={{
              display: 'block',
              marginBottom: 16,
              fontSize: 14,
              fontWeight: 700,
              color: '#ffffff',
              textTransform: 'uppercase',
              letterSpacing: '1.5px',
              opacity: 0.9
            }}>
              Clip Duration
            </label>
            <select 
              value={clipDuration} 
              onChange={e => setClipDuration(Number(e.target.value))}
              disabled={loading}
              style={{
                padding: '16px 24px',
                fontSize: 16,
                border: '2px solid rgba(255, 255, 255, 0.1)',
                borderRadius: 16,
                outline: 'none',
                backgroundColor: 'rgba(20, 20, 20, 0.95)',
                color: '#fff',
                cursor: 'pointer',
                fontFamily: 'inherit',
                minWidth: 180,
                transition: 'all 0.3s ease',
                backdropFilter: 'blur(10px)',
                appearance: 'none',
                WebkitAppearance: 'none',
                MozAppearance: 'none',
                boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
              }}
            >
              <option value={15}>15 seconds</option>
              <option value={30}>30 seconds</option>
              <option value={45}>45 seconds</option>
              <option value={60}>60 seconds</option>
            </select>
          </div>

          <button 
            onClick={handleAnalyze} 
            style={{ 
              padding: '20px 48px', 
              fontSize: 18,
              fontWeight: 700,
              background: loading 
                ? 'rgba(255, 255, 255, 0.1)' 
                : useViralCaptions 
                  ? 'linear-gradient(135deg, #ff0080 0%, #00ff88 100%)'
                  : 'linear-gradient(135deg, #00ff88 0%, #00ccff 100%)',
              color: loading ? '#666666' : '#000000',
              border: 'none',
              borderRadius: 20,
              cursor: loading ? 'not-allowed' : 'pointer',
              transition: 'all 0.4s ease',
              boxShadow: loading 
                ? 'none' 
                : useViralCaptions
                  ? '0 12px 40px rgba(255, 0, 128, 0.4), inset 0 1px 0 rgba(255, 255, 255, 0.2)'
                  : '0 12px 40px rgba(0, 255, 136, 0.4), inset 0 1px 0 rgba(255, 255, 255, 0.2)',
              transform: loading ? 'none' : 'translateY(0)',
              minWidth: 260,
              textTransform: 'uppercase',
              letterSpacing: '1.5px',
              position: 'relative',
              overflow: 'hidden'
            }}
            onMouseEnter={(e) => {
              if (!loading) {
                e.currentTarget.style.transform = 'translateY(-4px) scale(1.02)';
                e.currentTarget.style.boxShadow = useViralCaptions
                  ? '0 20px 50px rgba(255, 0, 128, 0.5), inset 0 1px 0 rgba(255, 255, 255, 0.2)'
                  : '0 20px 50px rgba(0, 255, 136, 0.5), inset 0 1px 0 rgba(255, 255, 255, 0.2)';
              }
            }}
            onMouseLeave={(e) => {
              if (!loading) {
                e.currentTarget.style.transform = 'translateY(0) scale(1)';
                e.currentTarget.style.boxShadow = useViralCaptions
                  ? '0 12px 40px rgba(255, 0, 128, 0.4), inset 0 1px 0 rgba(255, 255, 255, 0.2)'
                  : '0 12px 40px rgba(0, 255, 136, 0.4), inset 0 1px 0 rgba(255, 255, 255, 0.2)';
              }
            }}
            disabled={loading}
          >
            {loading ? 'â³ Processing...' : useViralCaptions ? 'ðŸš€ Generate VIRAL Clips' : 'ðŸŽ¬ Generate Clips'}
          </button>
        </div>

        {/* ðŸš€ VIRAL CAPTION SETTINGS */}
        <div style={{
          marginBottom: 48,
          padding: '40px',
          background: 'rgba(255, 0, 128, 0.05)',
          borderRadius: 24,
          border: '2px solid rgba(255, 0, 128, 0.2)',
          backdropFilter: 'blur(10px)'
        }}>
          <div style={{
            textAlign: 'center',
            marginBottom: 32
          }}>
            <div style={{
              display: 'inline-flex',
              alignItems: 'center',
              gap: 16,
              marginBottom: 16,
              padding: '12px 24px',
              background: 'rgba(255, 0, 128, 0.1)',
              borderRadius: 50,
              border: '1px solid rgba(255, 0, 128, 0.3)',
              backdropFilter: 'blur(10px)'
            }}>
              <span style={{ fontSize: 24 }}>ðŸš€</span>
              <span style={{ 
                fontSize: 14, 
                fontWeight: 600, 
                color: '#ff0080',
                textTransform: 'uppercase',
                letterSpacing: '2px'
              }}>
                Viral Caption System
              </span>
            </div>

            <h3 style={{
              fontSize: '2rem',
              fontWeight: 800,
              background: 'linear-gradient(135deg, #ff0080 0%, #00ff88 100%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              margin: '0 0 12px 0',
              letterSpacing: '-0.02em'
            }}>
              Ultra-Engaging Captions
            </h3>
            <p style={{
              fontSize: '1.1rem',
              color: '#cccccc',
              margin: 0,
              opacity: 0.8,
              maxWidth: 500,
              marginLeft: 'auto',
              marginRight: 'auto'
            }}>
              Add TikTok/Instagram Reels style captions that keep viewers hooked
            </p>
          </div>

          <div style={{
            display: 'flex',
            gap: 40,
            alignItems: 'center',
            justifyContent: 'center',
            flexWrap: 'wrap'
          }}>
            {/* Viral Captions Toggle */}
            <div style={{ textAlign: 'center' }}>
              <label style={{
                display: 'block',
                marginBottom: 16,
                fontSize: 14,
                fontWeight: 700,
                color: '#ffffff',
                textTransform: 'uppercase',
                letterSpacing: '1.5px'
              }}>
                Viral Captions
              </label>
              <button
                onClick={() => setUseViralCaptions(!useViralCaptions)}
                style={{
                  padding: '16px 32px',
                  fontSize: 16,
                  fontWeight: 700,
                  background: useViralCaptions 
                    ? 'linear-gradient(135deg, #ff0080 0%, #00ff88 100%)'
                    : 'rgba(255, 255, 255, 0.1)',
                  color: useViralCaptions ? '#000000' : '#ffffff',
                  border: `2px solid ${useViralCaptions ? 'transparent' : 'rgba(255, 255, 255, 0.2)'}`,
                  borderRadius: 16,
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  textTransform: 'uppercase',
                  letterSpacing: '1px',
                  minWidth: 160
                }}
              >
                {useViralCaptions ? 'âœ… ENABLED' : 'âŒ DISABLED'}
              </button>
            </div>

            {/* Caption Style */}
            {useViralCaptions && (
              <div style={{ textAlign: 'center' }}>
                <label style={{
                  display: 'block',
                  marginBottom: 16,
                  fontSize: 14,
                  fontWeight: 700,
                  color: '#ffffff',
                  textTransform: 'uppercase',
                  letterSpacing: '1.5px'
                }}>
                  Caption Style
                </label>
                <select 
                  value={captionStyle} 
                  onChange={e => setCaptionStyle(e.target.value)}
                  disabled={loading}
                  style={{
                    padding: '16px 24px',
                    fontSize: 16,
                    border: '2px solid rgba(255, 0, 128, 0.3)',
                    borderRadius: 16,
                    outline: 'none',
                    backgroundColor: 'rgba(20, 20, 20, 0.95)',
                    color: '#fff',
                    cursor: 'pointer',
                    fontFamily: 'inherit',
                    minWidth: 180,
                    transition: 'all 0.3s ease',
                    backdropFilter: 'blur(10px)',
                    appearance: 'none',
                    WebkitAppearance: 'none',
                    MozAppearance: 'none'
                  }}
                >
                  <option value="single-word">ðŸš€ Single Word (Ultra Viral)</option>
                  <option value="engaging">âœ¨ 1-2 Words (Engaging)</option>
                </select>
              </div>
            )}

            {/* Font Style */}
            {useViralCaptions && (
              <div style={{ textAlign: 'center' }}>
                <label style={{
                  display: 'block',
                  marginBottom: 16,
                  fontSize: 14,
                  fontWeight: 700,
                  color: '#ffffff',
                  textTransform: 'uppercase',
                  letterSpacing: '1.5px'
                }}>
                  Font Style
                </label>
                <select 
                  value={fontStyle} 
                  onChange={e => setFontStyle(e.target.value)}
                  disabled={loading}
                  style={{
                    padding: '16px 24px',
                    fontSize: 16,
                    border: '2px solid rgba(255, 0, 128, 0.3)',
                    borderRadius: 16,
                    outline: 'none',
                    backgroundColor: 'rgba(20, 20, 20, 0.95)',
                    color: '#fff',
                    cursor: 'pointer',
                    fontFamily: 'inherit',
                    minWidth: 160,
                    transition: 'all 0.3s ease',
                    backdropFilter: 'blur(10px)',
                    appearance: 'none',
                    WebkitAppearance: 'none',
                    MozAppearance: 'none'
                  }}
                >
                  <option value="impact">ðŸ’ª Impact (Bold & Sporty)</option>
                  <option value="default">ðŸ“± Default (Clean & Modern)</option>
                </select>
              </div>
            )}
          </div>

          {/* Preview Info */}
          {useViralCaptions && (
            <div style={{
              marginTop: 32,
              padding: '20px 28px',
              background: 'rgba(255, 0, 128, 0.1)',
              borderRadius: 16,
              border: '1px solid rgba(255, 0, 128, 0.2)',
              textAlign: 'center'
            }}>
              <p style={{
