import React, { useState, useRef } from 'react';
import './App.css';

interface Clip {
  id: number;
  title: string;
  description: string;
  start_time: string;
  end_time: string;
  duration: number;
  timestamp: number;
  filename: string;
  captionStyle?: string;
  fontStyle?: string;
  isViral?: boolean;
  fileSize?: number;
  failed?: boolean;
  error?: string;
  hook_type?: string;
  viral_score?: number;
  natural_flow_score?: number;
  quality_rating?: string;
  analysis_method?: string;
  post_analysis?: {
    quality_check: string;
    actual_content: string;
    language_confirmed: string;
  };
}

// SVG Icons as components
const SparkleIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
