const RocketIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M12 2L13.09 8.26L22 12L13.09 15.74L12 22L10.91 15.74L2 12L10.91 8.26L12 2Z"/>
  </svg>
);

const VideoIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M8 5v14l11-7z"/>
  </svg>
);

const ClockIcon = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
    <circle cx="12" cy="12" r="10"/>
    <polyline points="12,6 12,12 16,14"/>
  </svg>
);

const DownloadIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"/>
    <polyline points="7,10 12,15 17,10"/>
    <line x1="12" y1="15" x2="12" y2="3"/>
  </svg>
);

const LoadingIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor" className="animate-spin">
    <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" strokeDasharray="32" strokeDashoffset="32">
      <animate attributeName="stroke-dashoffset" dur="1s" values="32;0" repeatCount="indefinite"/>
    </circle>
  </svg>
);

const CheckIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <polyline points="20,6 9,17 4,12"/>
  </svg>
);

function App() {
  const [url, setUrl] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [clips, setClips] = useState<Clip[]>([]);
  const [numClips, setNumClips] = useState(3);
  const [clipDuration, setClipDuration] = useState(30);
  const [captionsEnabled, setCaptionsEnabled] = useState(true);
  const [captionStyle, setCaptionStyle] = useState('single-word');
  const [playingIndex, setPlayingIndex] = useState<number | null>(null);
  const [mutedIndex, setMutedIndex] = useState<number | null>(null);
  const videoRefs = useRef<(HTMLVideoElement | null)[]>([]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:5000/analyze-viral', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ytLink: url,
          numClips: numClips,
          clipDuration: clipDuration,
          captionStyle: captionsEnabled ? captionStyle : 'none',
          fontStyle: 'impact'
        }),
      });

      const data = await response.json();
      if (data.clips) {
        setClips(data.clips);
      }
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Example clips from actual generated content
  const exampleClips = [
    {
      title: "ðŸ”¥ Viral Business Insights",
      description: "AI-powered content that drives engagement",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751719352347_1.mp4",
      category: "Business"
    },
    {
      title: "ðŸ’¡ Game-Changing Strategies", 
      description: "Content optimized for maximum viral potential",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751719352347_2.mp4",
      category: "Strategy"
    },
    {
      title: "ðŸš€ Success Mindset Clips",
      description: "Engaging moments that capture attention",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751719352347_3.mp4",
      category: "Mindset"
    },
    {
      title: "âš¡ High-Impact Content",
      description: "Perfectly timed viral content creation",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751660345508_1.mp4",
      category: "Impact"
    },
    {
      title: "ðŸŽ¯ Targeted Messaging",
      description: "AI-selected peak engagement moments",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751660345508_2.mp4",
      category: "Marketing"
    },
    {
      title: "âœ¨ Premium Quality Clips",
      description: "Professional-grade viral content",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751660345508_3.mp4",
      category: "Quality"
    }
  ];

  return (
    <div className="app-container">
      {/* Header */}
      <header className="header">
        <div className="header-content">
          <div className="logo-container">
            <img src="/logo-removebg-preview.png" alt="Viral Clip Generator" className="logo-image" />
            <span className="brand-text">Trilion</span>
          </div>
          <nav className="nav-menu">
            <a href="#features" className="nav-link">Features</a>
            <a href="#examples" className="nav-link">Examples</a>
            <a href="#generate" className="nav-link cta-link">Generate Clips</a>
          </nav>
        </div>
      </header>

      {/* Hero Section */}
      <section className="hero">
        <div className="hero-background"></div>
        <div className="hero-content">
          <div className="hero-badge">
            <SparkleIcon />
            <span>AI-Powered Viral Content Creation</span>
          </div>
          <h1 className="hero-title">
            Transform Your Videos into
            <span className="gradient-text"> Viral Clips</span>
          </h1>
          <p className="hero-description">
            Our advanced AI analyzes your content to identify and extract the most engaging moments, 
            creating perfectly timed clips optimized for maximum viral potential on social media.
          </p>
          <div className="hero-stats">
            <div className="stat-item">
              <div className="stat-number">10M+</div>
              <div className="stat-label">Clips Generated</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">98%</div>
              <div className="stat-label">Accuracy Rate</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">50x</div>
              <div className="stat-label">Faster Creation</div>
            </div>
          </div>
        </div>
        <div className="hero-visual">
          <div className="floating-elements">
            <div className="floating-icon" style={{animationDelay: '0s'}}><VideoIcon /></div>
            <div className="floating-icon" style={{animationDelay: '2s'}}><RocketIcon /></div>
            <div className="floating-icon" style={{animationDelay: '4s'}}><SparkleIcon /></div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section id="features" className="features-section">
        <div className="features-container">
          <h2 className="section-title">Why Choose Our AI Clip Generator?</h2>
          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-icon gradient-bg-1">
                <SparkleIcon />
              </div>
              <h3>AI-Powered Analysis</h3>
              <p>Advanced machine learning identifies viral moments with 98% accuracy</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon gradient-bg-2">
                <RocketIcon />
              </div>
              <h3>Instant Generation</h3>
              <p>Create professional viral clips in seconds, not hours</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon gradient-bg-3">
                <VideoIcon />
              </div>
              <h3>Platform Optimized</h3>
              <p>Perfect formatting for TikTok, Instagram Reels, and YouTube Shorts</p>
            </div>
          </div>
        </div>
      </section>

      {/* Main Content */}
      <main className="main-content">
        {/* Input Section */}
        <section id="generate" className="generator-section">
          <div className="generator-container">
            <div className="generator-header">
              <h2 className="section-title">Create Your Viral Clips</h2>
              <p className="section-subtitle">
                Paste your YouTube URL and let our AI create engaging clips in seconds
              </p>
            </div>

            <form onSubmit={handleSubmit} className="generator-form">
              <div className="input-container">
                <div className="input-wrapper">
                  <input
                    type="text"
                    className="url-input"
                    placeholder="Paste your YouTube URL here..."
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    required
                  />
                  <div className="input-icon">
                    <VideoIcon />
                  </div>
                </div>
                <button type="submit" className="submit-button" disabled={isLoading || !url}>
                  {isLoading ? (
                    <>
                      <LoadingIcon />
                      <span>Processing...</span>
                    </>
                  ) : (
                    <>
                      <RocketIcon />
                      <span>Create Viral Clips</span>
                    </>
                  )}
                </button>
              </div>

              {/* Options Section */}
              <div className="options-container">
                <div className="option-group">
                  <label className="option-label">
                    <ClockIcon />
                    Number of Clips
                  </label>
                  <select 
                    className="option-select"
                    value={numClips}
                    onChange={(e) => setNumClips(parseInt(e.target.value))}
                  >
                    <option value={1}>1 clip</option>
                    <option value={2}>2 clips</option>
                    <option value={3}>3 clips</option>
                    <option value={4}>4 clips</option>
                    <option value={5}>5 clips</option>
                  </select>
                </div>

                <div className="option-group">
                  <label className="option-label">
                    <ClockIcon />
                    Clip Duration
                  </label>
                  <select 
                    className="option-select"
                    value={clipDuration}
                    onChange={(e) => setClipDuration(parseInt(e.target.value))}
                  >
                    <option value={15}>15 seconds</option>
                    <option value={30}>30 seconds</option>
                    <option value={60}>60 seconds</option>
                  </select>
                </div>

                <div className="option-group">
                  <label className="option-label">
                    <SparkleIcon />
                    Captions
                  </label>
                  <div className="toggle-container">
                    <label className="toggle-switch">
                      <input
                        type="checkbox"
                        checked={captionsEnabled}
                        onChange={(e) => setCaptionsEnabled(e.target.checked)}
                      />
                      <span className="toggle-slider"></span>
                    </label>
                    <span className="toggle-text">{captionsEnabled ? 'On' : 'Off'}</span>
                  </div>
                </div>

                {captionsEnabled && (
                  <div className="option-group">
                    <label className="option-label">
                      <SparkleIcon />
                      Caption Style
                    </label>
                    <select 
                      className="option-select"
                      value={captionStyle}
                      onChange={(e) => setCaptionStyle(e.target.value)}
                    >
                      <option value="single-word">Single Word</option>
                      <option value="engaging">Engaging Phrases</option>
                    </select>
                  </div>
                )}
              </div>
            </form>
          </div>
        </section>

        {/* Loading State */}
        {isLoading && (
          <section className="loading-section">
            <div className="loading-container">
              <div className="loading-animation">
                <div className="loading-spinner"></div>
                <div className="loading-dots">
                  <div className="dot"></div>
                  <div className="dot"></div>
                  <div className="dot"></div>
                </div>
              </div>
              <h3>Creating Your Viral Clips</h3>
              <p>Our AI is analyzing your video and extracting the most engaging moments...</p>
              <div className="progress-steps">
                <div className="step active">
                  <CheckIcon />
                  <span>Video Downloaded</span>
                </div>
                <div className="step active">
                  <LoadingIcon />
                  <span>AI Analysis</span>
                </div>
                <div className="step">
                  <span>Clip Generation</span>
                </div>
              </div>
            </div>
          </section>
        )}

        {/* Generated Clips Results */}
        {clips.length > 0 && (
          <section className="results-section">
            <div className="results-container">
              <div className="results-header">
                <h2 className="section-title">Your Viral Clips Are Ready! ðŸŽ‰</h2>
                <p className="section-subtitle">
                  AI-powered clips optimized for maximum engagement
                </p>
              </div>
              <div className="results-grid">
                {clips.map((clip, index) => (
                  <div key={index} className="result-card">
                    <div className="result-video-container">
                      <video className="result-video" controls>
                        <source src={`http://localhost:5000/clips/${clip.filename}`} type="video/mp4" />
                        Your browser does not support the video tag.
                      </video>
                      <div className="video-overlay">
                        <div className="quality-badge">
                          <SparkleIcon />
                          AI Selected
                        </div>
                      </div>
                    </div>
                    <div className="result-info">
                      <h3>ðŸŽ¬ Clip {index + 1}</h3>
                      <p>{clip.description || 'AI-generated viral clip optimized for engagement'}</p>
                      <div className="result-actions">
                        <a 
                          href={`http://localhost:5000/download/${clip.filename}`}
                          className="action-button download"
                          download
                        >
                          <DownloadIcon />
                          Download
                        </a>
                        <button className="action-button share">
                          <SparkleIcon />
                          Share
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </section>
        )}

        {/* Example Clips Carousel */}
        <section id="examples" className="carousel-section">
          <div className="carousel-container">
            <div className="carousel-header">
              <h2 className="section-title">See Our AI in Action</h2>
              <p className="section-subtitle">
                Real clips generated by our AI from actual YouTube videos
              </p>
            </div>

            <div className="carousel-wrapper">
              <div className="carousel-track">
                {[...exampleClips, ...exampleClips].map((clip, index) => (
                  <div key={index} className="carousel-card">
                    <div className="carousel-video-container">
                      <video
                        className="carousel-video"
                        ref={el => { videoRefs.current[index] = el; }}
                        muted={mutedIndex !== index}
                        controls={false}
                        playsInline
                        onClick={() => {
                          const video = videoRefs.current[index];
                          if (!video) return;
                          if (playingIndex === index) {
                            video.pause();
                            setPlayingIndex(null);
                          } else {
                            videoRefs.current.forEach((v, i) => {
                              if (v && i !== index) v.pause();
                            });
                            video.play();
                            setPlayingIndex(index);
                            setMutedIndex(index);
                            video.muted = false;
                          }
                        }}
                        onPlay={() => {
                          setPlayingIndex(index);
                          setMutedIndex(index);
                        }}
                        onPause={() => {
                          setPlayingIndex(null);
                        }}
                      >
                        <source src={clip.videoUrl} type="video/mp4" />
                      </video>
                      <div className="carousel-overlay" style={{opacity: 1}}>
                        <div className="category-badge">{clip.category}</div>
                        <button
                          className="play-button"
                          onClick={e => {
                            e.preventDefault();
                            e.stopPropagation();
                            const video = videoRefs.current[index];
                            if (!video) return;
                            if (playingIndex === index && !video.paused) {
                              video.pause();
                              setPlayingIndex(null);
                            } else {
                              videoRefs.current.forEach((v, i) => {
                                if (v && i !== index) v.pause();
                              });
                              video.play();
                              setPlayingIndex(index);
                              setMutedIndex(index);
                              video.muted = false;
                            }
                          }}
                        >
                          {playingIndex === index ? (
                            <svg width="32" height="32" viewBox="0 0 24 24" fill="currentColor"><rect x="6" y="4" width="4" height="16"/><rect x="14" y="4" width="4" height="16"/></svg>
                          ) : (
                            <svg width="32" height="32" viewBox="0 0 24 24" fill="currentColor"><polygon points="5,3 19,12 5,21"/></svg>
                          )}
                        </button>
                      </div>
                    </div>
                    <div className="carousel-info">
                      <h4>{clip.title}</h4>
                      <p>{clip.description}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </section>
      </main>
    </div>
  );
}

export default App;
