import React, { useState } from 'react';
import './App.css';

interface Clip {
  id: number;
  title: string;
  description: string;
  start_time: string;
  end_time: string;
  duration: number;
  timestamp: number;
  filename: string;
}

function App() {
  const [ytLink, setYtLink] = useState('');
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState('');
  const [clips, setClips] = useState<Clip[]>([]);
  const [numClips, setNumClips] = useState(3);
  const [clipDuration, setClipDuration] = useState(30);

  const handleAnalyze = async () => {
    if (!ytLink) {
      setStatus('Please enter a YouTube link');
      return;
    }

    setLoading(true);
    setStatus('Processing video...');
    setClips([]);

    try {
      const response = await fetch('http://localhost:5000/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          ytLink,
          numClips,
          clipDuration
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setStatus('Viral clip analysis completed! Use the timestamps to create your clips.');
        setClips(data.clips || []);
      } else {
        setStatus(`Error: ${data.error}`);
      }
    } catch (error) {
      setStatus('Error connecting to server');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App" style={{ 
      minHeight: '100vh',
      background: '#000000',
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
      padding: '40px 20px',
      color: '#ffffff'
    }}>
      <div style={{
        maxWidth: 1200,
        margin: '0 auto',
        background: '#111111',
        borderRadius: 24,
        padding: 48,
        boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.8)',
        border: '1px solid #333333'
      }}>
        <div style={{ textAlign: 'center', marginBottom: 48 }}>
          <h1 style={{
            fontSize: '3rem',
            fontWeight: 800,
            background: 'linear-gradient(135deg, #00ff88 0%, #00ccff 50%, #ff0080 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            margin: '0 0 16px 0',
            letterSpacing: '-0.03em',
            textShadow: '0 0 30px rgba(0, 255, 136, 0.3)'
          }}>
            üé¨ Viral Clip Generator
          </h1>
          <p style={{
            fontSize: '1.2rem',
            color: '#cccccc',
            margin: 0,
            fontWeight: 400,
            opacity: 0.8
          }}>
            Transform YouTube videos into viral social media clips
          </p>
        </div>

        <div style={{ marginBottom: 40 }}>
          <div style={{
            position: 'relative',
            maxWidth: 700,
            margin: '0 auto'
          }}>
            <input
              type="text"
              placeholder="Paste YouTube video link here..."
              value={ytLink}
              onChange={e => setYtLink(e.target.value)}
              style={{
                width: '100%',
                padding: '20px 24px',
                fontSize: 18,
                border: '2px solid #333333',
                borderRadius: 16,
                outline: 'none',
                transition: 'all 0.3s ease',
                fontFamily: 'inherit',
                backgroundColor: '#000000',
                color: '#ffffff',
                boxShadow: '0 4px 20px rgba(0, 0, 0, 0.5)'
              }}
              onFocus={(e) => {
                e.target.style.borderColor = '#00ff88';
                e.target.style.boxShadow = '0 0 0 4px rgba(0, 255, 136, 0.2), 0 4px 20px rgba(0, 0, 0, 0.5)';
              }}
              onBlur={(e) => {
                e.target.style.borderColor = '#333333';
                e.target.style.boxShadow = '0 4px 20px rgba(0, 0, 0, 0.5)';
              }}
              disabled={loading}
            />
          </div>
        </div>

        <div style={{
          display: 'flex',
          gap: 32,
          alignItems: 'center',
          justifyContent: 'center',
          flexWrap: 'wrap',
          marginBottom: 40
        }}>
          <div style={{ textAlign: 'center' }}>
            <label style={{
              display: 'block',
              marginBottom: 12,
              fontSize: 16,
              fontWeight: 600,
              color: '#ffffff',
              textTransform: 'uppercase',
              letterSpacing: '0.5px'
            }}>
              Number of Clips
            </label>
            <select 
              value={numClips} 
              onChange={e => setNumClips(Number(e.target.value))}
              disabled={loading}
              style={{
                padding: '16px 20px',
                fontSize: 16,
                border: '2px solid #333333',
                borderRadius: 12,
                outline: 'none',
                backgroundColor: '#000000',
                color: '#ffffff',
                cursor: 'pointer',
                fontFamily: 'inherit',
                minWidth: 140,
                transition: 'all 0.3s ease'
              }}
            >
              <option value={1}>1 Clip</option>
              <option value={2}>2 Clips</option>
              <option value={3}>3 Clips</option>
              <option value={4}>4 Clips</option>
              <option value={5}>5 Clips</option>
            </select>
          </div>

          <div style={{ textAlign: 'center' }}>
            <label style={{
              display: 'block',
              marginBottom: 12,
              fontSize: 16,
              fontWeight: 600,
              color: '#ffffff',
              textTransform: 'uppercase',
              letterSpacing: '0.5px'
            }}>
              Clip Duration
            </label>
            <select 
              value={clipDuration} 
              onChange={e => setClipDuration(Number(e.target.value))}
              disabled={loading}
              style={{
                padding: '16px 20px',
                fontSize: 16,
                border: '2px solid #333333',
                borderRadius: 12,
                outline: 'none',
                backgroundColor: '#000000',
                color: '#ffffff',
                cursor: 'pointer',
                fontFamily: 'inherit',
                minWidth: 160,
                transition: 'all 0.3s ease'
              }}
            >
              <option value={15}>15 seconds</option>
              <option value={30}>30 seconds</option>
              <option value={45}>45 seconds</option>
              <option value={60}>60 seconds</option>
            </select>
          </div>

          <button 
            onClick={handleAnalyze} 
            style={{ 
              padding: '20px 40px', 
              fontSize: 18,
              fontWeight: 700,
              background: loading 
                ? '#333333' 
                : 'linear-gradient(135deg, #00ff88 0%, #00ccff 100%)',
              color: loading ? '#666666' : '#000000',
              border: 'none',
              borderRadius: 16,
              cursor: loading ? 'not-allowed' : 'pointer',
              transition: 'all 0.3s ease',
              boxShadow: loading 
                ? 'none' 
                : '0 8px 25px rgba(0, 255, 136, 0.4)',
              transform: loading ? 'none' : 'translateY(0)',
              minWidth: 220,
              textTransform: 'uppercase',
              letterSpacing: '1px'
            }}
            onMouseEnter={(e) => {
              if (!loading) {
                e.currentTarget.style.transform = 'translateY(-3px)';
                e.currentTarget.style.boxShadow = '0 12px 35px rgba(0, 255, 136, 0.5)';
              }
            }}
            onMouseLeave={(e) => {
              if (!loading) {
                e.currentTarget.style.transform = 'translateY(0)';
                e.currentTarget.style.boxShadow = '0 8px 25px rgba(0, 255, 136, 0.4)';
              }
            }}
            disabled={loading}
          >
            {loading ? '‚è≥ Processing...' : 'üöÄ Generate Viral Clips'}
          </button>
        </div>

        <div style={{ marginTop: 32, minHeight: 40, textAlign: 'center' }}>
          {status && (
            <div style={{
              padding: '16px 24px',
              borderRadius: 12,
              fontSize: 16,
              fontWeight: 600,
              backgroundColor: status.includes('Error') ? 'rgba(255, 0, 0, 0.1)' : 'rgba(0, 255, 136, 0.1)',
              color: status.includes('Error') ? '#ff6666' : '#00ff88',
              border: `2px solid ${status.includes('Error') ? 'rgba(255, 0, 0, 0.3)' : 'rgba(0, 255, 136, 0.3)'}`,
              backdropFilter: 'blur(10px)'
            }}>
              {status}
            </div>
          )}
        </div>

        {clips.length > 0 && (
          <div style={{ marginTop: 56 }}>
            <h2 style={{
              fontSize: '2.2rem',
              fontWeight: 800,
              color: '#ffffff',
              margin: '0 0 40px 0',
              textAlign: 'center',
              textTransform: 'uppercase',
              letterSpacing: '1px'
            }}>
              üéØ Your Viral Clips ({clips.length})
            </h2>
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(380px, 1fr))',
              gap: 32
            }}>
              {clips.map((clip, index) => (
                <div key={index} style={{ 
                  background: '#000000',
                  border: '2px solid #333333',
                  borderRadius: 20,
                  padding: 32,
                  boxShadow: '0 8px 32px rgba(0, 0, 0, 0.6)',
                  transition: 'all 0.4s ease',
                  position: 'relative',
                  overflow: 'hidden'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = 'translateY(-8px) scale(1.02)';
                  e.currentTarget.style.boxShadow = '0 20px 40px rgba(0, 0, 0, 0.8)';
                  e.currentTarget.style.borderColor = '#00ff88';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'translateY(0) scale(1)';
                  e.currentTarget.style.boxShadow = '0 8px 32px rgba(0, 0, 0, 0.6)';
                  e.currentTarget.style.borderColor = '#333333';
                }}>
                  <div style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    height: 6,
                    background: 'linear-gradient(90deg, #00ff88 0%, #00ccff 50%, #ff0080 100%)'
                  }} />

                  <h3 style={{
                    margin: '0 0 16px 0',
                    fontSize: '1.3rem',
                    fontWeight: 700,
                    color: '#ffffff',
                    lineHeight: 1.4,
                    textTransform: 'uppercase',
                    letterSpacing: '0.5px'
                  }}>
                    {clip.title}
                  </h3>

                  <p style={{
                    margin: '0 0 20px 0',
                    fontSize: 16,
                    color: '#00ff88',
                    fontWeight: 600,
                    textTransform: 'uppercase',
                    letterSpacing: '1px'
                  }}>
                    ‚è±Ô∏è Duration: {clip.start_time} - {clip.end_time}
                  </p>

                  <p style={{
                    margin: '0 0 24px 0',
                    fontSize: 15,
                    color: '#cccccc',
                    lineHeight: 1.7,
                    fontWeight: 400
                  }}>
                    {clip.description}
                  </p>

                  <video 
                    controls 
                    style={{
                      width: '100%',
                      maxHeight: 220,
                      borderRadius: 12,
                      marginBottom: 20,
                      boxShadow: '0 4px 20px rgba(0, 0, 0, 0.8)',
                      backgroundColor: '#000000',
                      border: '1px solid #333333'
                    }}
                    src={`http://localhost:5000/clips/${clip.filename}`}
                  />

                  <a
                    href={`http://localhost:5000/download/${clip.filename}`}
                    download
                    style={{
                      display: 'inline-flex',
                      alignItems: 'center',
                      gap: 12,
                      padding: '16px 28px',
                      backgroundColor: '#ff0080',
                      color: '#ffffff',
                      border: 'none',
                      borderRadius: 12,
                      cursor: 'pointer',
                      fontSize: 16,
                      fontWeight: 700,
                      textDecoration: 'none',
                      transition: 'all 0.3s ease',
                      boxShadow: '0 4px 15px rgba(255, 0, 128, 0.4)',
                      textTransform: 'uppercase',
                      letterSpacing: '1px'
                    }}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.backgroundColor = '#cc0066';
                      e.currentTarget.style.transform = 'translateY(-2px)';
                      e.currentTarget.style.boxShadow = '0 8px 25px rgba(255, 0, 128, 0.6)';
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.backgroundColor = '#ff0080';
                      e.currentTarget.style.transform = 'translateY(0)';
                      e.currentTarget.style.boxShadow = '0 4px 15px rgba(255, 0, 128, 0.4)';
                    }}
                  >
                    üì• Download Clip
                  </a>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
