import React, { useState } from 'react';
import './App.css';

interface Clip {
  id: number;
  title: string;
  description: string;
  start_time: string;
  end_time: string;
  duration: number;
  timestamp: number;
  filename: string;
  captionStyle?: string;
  fontStyle?: string;
  isViral?: boolean;
  fileSize?: number;
  failed?: boolean;
  error?: string;
  hook_type?: string;
  viral_score?: number;
  natural_flow_score?: number;
  quality_rating?: string;
  analysis_method?: string;
  post_analysis?: {
    quality_check: string;
    actual_content: string;
    language_confirmed: string;
  };
}

// SVG Icons as components
const SparkleIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M12 0l3.09 6.26L22 9.27l-5.45 5.32L18.18 24 12 20.27 5.82 24l1.63-9.41L2 9.27l6.91-3.01L12 0z"/>
  </svg>
);

const RocketIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M12 2L13.09 8.26L22 12L13.09 15.74L12 22L10.91 15.74L2 12L10.91 8.26L12 2Z"/>
  </svg>
);

const VideoIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M8 5v14l11-7z"/>
  </svg>
);

const ClockIcon = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
    <circle cx="12" cy="12" r="10"/>
    <polyline points="12,6 12,12 16,14"/>
  </svg>
);

const DownloadIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"/>
    <polyline points="7,10 12,15 17,10"/>
    <line x1="12" y1="15" x2="12" y2="3"/>
  </svg>
);

const FolderIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <path d="M22 19a2 2 0 0 1-2 2H4a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h5l2 3h9a2 2 0 0 1 2 2z"/>
  </svg>
);

const LoadingIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor" className="animate-spin">
    <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" strokeDasharray="32" strokeDashoffset="32">
      <animate attributeName="stroke-dashoffset" dur="1s" values="32;0" repeatCount="indefinite"/>
    </circle>
  </svg>
);

const CheckIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <polyline points="20,6 9,17 4,12"/>
  </svg>
);

const CloseIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="currentColor">
    <line x1="18" y1="6" x2="6" y2="18"/>
    <line x1="6" y1="6" x2="18" y2="18"/>
  </svg>
);

function App() {
  const [url, setUrl] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [clips, setClips] = useState<Clip[]>([]);
  const [numClips, setNumClips] = useState(3);
  const [clipDuration, setClipDuration] = useState(30);
  const [captionsEnabled, setCaptionsEnabled] = useState(true);
  const [captionStyle, setCaptionStyle] = useState('single-word');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:5000/analyze-viral', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ytLink: url,
          numClips: numClips,
          clipDuration: clipDuration,
          captionStyle: captionsEnabled ? captionStyle : 'none',
          fontStyle: 'impact'
        }),
      });

      const data = await response.json();
      if (data.clips) {
        setClips(data.clips);
      }
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Example clips from actual generated content
  const exampleClips = [
    {
      title: "ðŸ”¥ Viral Business Insights",
      description: "AI-powered content that drives engagement",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751719352347_1.mp4",
      category: "Business"
    },
    {
      title: "ðŸ’¡ Game-Changing Strategies", 
      description: "Content optimized for maximum viral potential",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751719352347_2.mp4",
      category: "Strategy"
    },
    {
      title: "ðŸš€ Success Mindset Clips",
      description: "Engaging moments that capture attention",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751719352347_3.mp4",
      category: "Mindset"
    },
    {
      title: "âš¡ High-Impact Content",
      description: "Perfectly timed viral content creation",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751660345508_1.mp4",
      category: "Impact"
    },
    {
      title: "ðŸŽ¯ Targeted Messaging",
      description: "AI-selected peak engagement moments",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751660345508_2.mp4",
      category: "Marketing"
    },
    {
      title: "âœ¨ Premium Quality Clips",
      description: "Professional-grade viral content",
      videoUrl: "http://localhost:5000/clips/VIRAL_single-word_1751660345508_3.mp4",
      category: "Quality"
    }
  ];

  return (
    <div className="app-container">
      {/* Header */}
      <header className="header">
        <div className="header-content">
          <div className="logo-container">
            <img src="/logo-removebg-preview.png" alt="Viral Clip Generator" className="logo-image" />
            <span className="brand-text">ClipGenius</span>
          </div>
          <nav className="nav-menu">
            <a href="#features" className="nav-link">Features</a>
            <a href="#examples" className="nav-link">Examples</a>
            <a href="#generate" className="nav-link cta-link">Generate Clips</a>
          </nav>
        </div>
      </header>

      {/* Hero Section */}
      <section className="hero">
        <div className="hero-background"></div>
        <div className="hero-content">
          <div className="hero-badge">
            <SparkleIcon />
            <span>AI-Powered Viral Content Creation</span>
          </div>
          <h1 className="hero-title">
            Transform Your Videos into
            <span className="gradient-text"> Viral Clips</span>
          </h1>
          <p className="hero-description">
            Our advanced AI analyzes your content to identify and extract the most engaging moments, 
            creating perfectly timed clips optimized for maximum viral potential on social media.
          </p>
          <div className="hero-stats">
            <div className="stat-item">
              <div className="stat-number">10M+</div>
              <div className="stat-label">Clips Generated</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">98%</div>
              <div className="stat-label">Accuracy Rate</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">50x</div>
              <div className="stat-label">Faster Creation</div>
            </div>
          </div>
        </div>
        <div className="hero-visual">
          <div className="floating-elements">
            <div className="floating-icon" style={{animationDelay: '0s'}}><VideoIcon /></div>
            <div className="floating-icon" style={{animationDelay: '2s'}}><RocketIcon /></div>
            <div className="floating-icon" style={{animationDelay: '4s'}}><SparkleIcon /></div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section id="features" className="features-section">
        <div className="features-container">
          <h2 className="section-title">Why Choose Our AI Clip Generator?</h2>
          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-icon gradient-bg-1">
                <SparkleIcon />
              </div>
              <h3>AI-Powered Analysis</h3>
              <p>Advanced machine learning identifies viral moments with 98% accuracy</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon gradient-bg-2">
                <RocketIcon />
              </div>
              <h3>Instant Generation</h3>
              <p>Create professional viral clips in seconds, not hours</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon gradient-bg-3">
                <VideoIcon />
              </div>
              <h3>Platform Optimized</h3>
              <p>Perfect formatting for TikTok, Instagram Reels, and YouTube Shorts</p>
            </div>
          </div>
        </div>
      </section>

      {/* Main Content */}
      <main className="main-content">
        {/* Input Section */}
        <section className="input-section">
          <form onSubmit={handleSubmit}>
            <div className="input-container">
              <input
                type="text"
                className="url-input"
                placeholder="Paste your YouTube URL here..."
                value={url}
                onChange={(e) => setUrl(e.target.value)}
              />
              <button type="submit" className="submit-button" disabled={isLoading}>
                {isLoading ? 'Processing...' : 'Create Viral Clips'}
              </button>
            </div>

            {/* Options Section */}
            <div className="options-container">
              <div className="option-group">
                <label className="option-label">Number of Clips</label>
                <select 
                  className="option-select"
                  value={numClips}
                  onChange={(e) => setNumClips(parseInt(e.target.value))}
                >
                  <option value={1}>1 clip</option>
                  <option value={2}>2 clips</option>
                  <option value={3}>3 clips</option>
                  <option value={4}>4 clips</option>
                  <option value={5}>5 clips</option>
                </select>
              </div>

              <div className="option-group">
                <label className="option-label">Clip Duration</label>
                <select 
                  className="option-select"
                  value={clipDuration}
                  onChange={(e) => setClipDuration(parseInt(e.target.value))}
                >
                  <option value={15}>15 seconds</option>
                  <option value={30}>30 seconds</option>
                  <option value={60}>60 seconds</option>
                </select>
              </div>

              <div className="option-group">
                <label className="option-label">Captions</label>
                <div className="toggle-container">
                  <label className="toggle-switch">
                    <input
                      type="checkbox"
                      checked={captionsEnabled}
                      onChange={(e) => setCaptionsEnabled(e.target.checked)}
                    />
                    <span className="toggle-slider"></span>
                  </label>
                  <span className="toggle-text">{captionsEnabled ? 'On' : 'Off'}</span>
                </div>
              </div>

              {captionsEnabled && (
                <div className="option-group">
                  <label className="option-label">Caption Style</label>
                  <select 
                    className="option-select"
                    value={captionStyle}
                    onChange={(e) => setCaptionStyle(e.target.value)}
                  >
                    <option value="single-word">Single Word</option>
                    <option value="engaging">Engaging Phrases</option>
                  </select>
                </div>
              )}
            </div>
          </form>
        </section>

        {/* Generated Clips Section */}
        {isLoading && (
          <div className="loading">
            <div className="loading-spinner"></div>
          </div>
        )}

        {clips.length > 0 && (
          <section className="example-section">
            <div className="example-header">
              <h2>Your Generated Clips</h2>
            </div>
            <div className="example-grid">
              {clips.map((clip, index) => (
                <div key={index} className="example-card">
                  <video className="example-video" controls>
                    <source src={`http://localhost:5000/clips/${clip.filename}`} type="video/mp4" />
                    Your browser does not support the video tag.
                  </video>
                  <div className="example-info">
                    <h3>Clip {index + 1}</h3>
                    <p>{clip.description || 'AI-generated viral clip'}</p>
                  </div>
                </div>
              ))}
            </div>
          </section>
        )}

        {/* Example Section */}
        <section className="example-section">
          <div className="example-header">
            <h2>Example Clips</h2>
            <p>See how our AI transforms videos into engaging viral content</p>
          </div>
          <div className="example-grid">
            {exampleClips.map((clip, index) => (
              <div key={index} className="example-card">
                <video className="example-video" controls>
                  <source src={clip.videoUrl} type="video/mp4" />
                  Your browser does not support the video tag.
                </video>
                <div className="example-info">
                  <h3>{clip.title}</h3>
                  <p>{clip.description}</p>
                </div>
              </div>
            ))}
      </div>
        </section>
      </main>
    </div>
  );
}

export default App;
